import React, { useEffect, useState } from 'react';
import { useParams } from "react-router-dom";
import api from '../utils/api';
import { io } from 'socket.io-client';

const Field = () => {
    const { id } = useParams();
    const [field, setField] = useState(null);
    const [points, setPoints] = useState([]);
    const [socket, setSocket] = useState(null);  // Стейт для WebSocket

    // Вибір поля
    const selectField = async () => {
        try {
            await api.post('/fields/select/', { fieldId: id });
        } catch (err) {
            console.error('Не вдалося вибрати поле:', err);
        }
    };

    // Деселект поля
    const deselectField = async () => {
        try {
            await api.post('/fields/deselect/', { fieldId: id });
        } catch (err) {
            console.error('Не вдалося деселектнути поле:', err);
        }
    };

    // Завантаження точок
    const fetchPoints = async () => {
        try {
            const response = await api.get('/measurement-points/points');
            setPoints(response.data.points);
        } catch (error) {
            console.error("Error fetching points:", error);
        }
    };

    // Активація точки
    const activatePoint = async (pointId) => {
        try {
            await api.post('/measurement-points/activate', { pointId });
        } catch (err) {
            console.error('Помилка при активації точки:', err);
        }
    };

    // Деактивація точки
    const deactivatePoint = async (pointId) => {
        try {
            await api.post('/measurement-points/deactivate', { pointId });
        } catch (err) {
            console.error('Помилка при деактивації точки:', err);
        }
    };

    // Ініціалізація поля та точок
    useEffect(() => {
        const init = async () => {
            try {
                setPoints([]); // Очистити точки при зміні поля

                const fieldResponse = await api.get(`/fields/${id}`);
                setField(fieldResponse.data);

                await selectField();
                await fetchPoints(); // лише для первинного завантаження
            } catch (err) {
                console.error("Помилка ініціалізації поля:", err);
            }
        };

        init();

        // 🔽 ЦЕ ВАЖЛИВО: при переході на інше поле — деселект попереднього
        return () => {
            deselectField();
        };
    }, [id]);

    // Підключення до WebSocket
    useEffect(() => {
        const socket = io('http://localhost:5000', { transports: ['websocket'] });
        setSocket(socket);

        socket.on('connect', () => {
            console.log('Socket.io підключено');
            socket.emit('subscribe', { fieldId: id }); // emit подію підписки
        });

        socket.on('update', (data) => {
            console.log('Update from server:', data);

            if (data.type === 'pointActivated') {
            setPoints((prev) =>
                prev.map(p => p.id === data.pointId ? { ...p, active: true } : p)
            );} else if (data.type === 'pointDeactivated') {
                console.log('Point deactivated:', data);
                setPoints((prev) =>
                    prev.map(p => p.id === data.pointId ? { ...p, active: false } : p)
                );
            }

        });

        socket.on('disconnect', () => {
            console.log('Socket.io відключено');
        });

        return () => {
            socket.disconnect(); // Закриваємо з'єднання при виході
        };
    }, [id]);


    if (!field) return <div className="p-4">Завантаження...</div>;

    return (
        <div className="p-6 w-full flex-1">
            <h1 className="text-3xl font-bold mb-2">{field.name}</h1>
            <p className="text-gray-600 mb-4">{field.description}</p>

            <div className="grid grid-cols-2 gap-4">
                <p><strong>Площа:</strong> {field.area} га</p>
                <p><strong>Створено:</strong> {new Date(field.created_at).toLocaleDateString()}</p>
            </div>

            <hr className="my-6" />

            <h2 className="text-xl font-semibold mb-4 mt-6">Точки поля</h2>

            <div className="max-h-96 overflow-y-auto pr-2 border rounded-lg shadow-sm bg-white">
                {points.length === 0 ? (
                    <p className="p-4 text-gray-500 italic">Завантаження точок або точок немає.</p>
                ) : (
                    <ul className="divide-y divide-gray-200">
                        {points.map((point) => (
                            <PointItem
                                key={point.id}
                                point={point}
                                onActivate={activatePoint}
                                onDeactivate={deactivatePoint}
                            />
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
};

// Компонент для відображення кожної точки
const PointItem = React.memo(({ point, onActivate, onDeactivate }) => {
    return (
        <li key={point.id} className="flex items-center px-4 py-3 hover:bg-gray-50">
            <div className="h-6 px-3 flex-shrink-0 text-center text-l font-semibold text-gray-700 bg-gray-100 shadow-md rounded-md whitespace-nowrap">
                №{point.point_order}
            </div>

            <div className="w-px h-16 bg-gray-300 mx-4"></div>

            <div className="flex-1">
                <div className="flex justify-between items-center ">
                    <div className="text-l text-gray-800">
                        lat: {point.latitude.toFixed(6)}
                    </div>
                    <div className="text-sm text-gray-800">
                        Статус:{" "}
                        <span className={point.active ? "text-green-600" : "text-red-500"}>
                            {point.active ? "Активна" : "Неактивна"}
                        </span>
                    </div>
                    <button
                        onClick={() =>
                            point.active ? onDeactivate(point.id) : onActivate(point.id)
                        }
                        className={`text-sm px-3 py-1 rounded ${
                            point.active
                                ? "bg-red-100 text-red-600 hover:bg-red-200"
                                : "bg-green-100 text-green-600 hover:bg-green-200"
                        }`}
                    >
                        {point.active ? "Деактивувати" : "Активувати"}
                    </button>
                </div>

                <div className="mt-1 text-sm text-gray-600">
                    lng: {point.longitude.toFixed(6)}
                </div>
            </div>
        </li>
    );
});

export default Field;
