import React from 'react';
import AuthForm from '../components/AuthForm';
import api from '../utils/api';
import { useNavigate } from 'react-router-dom';
import response from "express-session/session/cookie";

export default function RegisterPage() {
    const navigate = useNavigate();

    const handleRegister = async (email, password) => {
        try {
            const res = await api.post('/auth/register/password', { email, password });
            console.log('Register success:', res.data);
            navigate('/login');
        } catch (err) {
            alert('Помилка реєстрації: ' + err.response?.data?.message || err.message);
        }
    };
// Зберігаємо ім’я користувача
    localStorage.setItem('userName', response.data.user.name);
    return <AuthForm type="register" onSubmit={handleRegister} />;
}
