import React, { useEffect, useState } from 'react';
import api from '../../utils/api';

const AdminUserDevices = () => {
    const [users, setUsers] = useState([]);
    const [devices, setDevices] = useState([]);
    const [associations, setAssociations] = useState([]);
    const [newAssociation, setNewAssociation] = useState({ user_id: '', iot_device_id: '' });

    const fetchUsers = async () => {
        const res = await api.get('/users');
        setUsers(res.data);
    };

    const fetchDevices = async () => {
        try {
            const res = await api.get('/iot-devices', { withCredentials: true });
            setDevices(res.data);
        } catch (err) {
            console.error('Error fetching devices:', err);
        }
    };

    const fetchAssociations = async () => {
        const res = await api.get('/user-iot-devices/all');
        setAssociations(res.data);
    };

    useEffect(() => {
        fetchUsers();
        fetchDevices();
        fetchAssociations();
    }, []);

    const handleBind = async () => {
        try {
            await api.post('/user-iot-devices', newAssociation);
            setNewAssociation({ user_id: '', iot_device_id: '' });
            fetchAssociations();
        } catch (error) {
            console.error('Помилка при прив’язці пристрою:', error);
        }
    };

    const handleUnbind = async (association) => {
        try {
            await api.delete('/user-iot-devices', {
                data: {
                    user_id: association.user_id,
                    iot_device_id: association.iot_device_id
                }
            });
            fetchAssociations();
        } catch (error) {
            console.error('Помилка при відв’язці пристрою:', error);
        }
    };

    // ... (початок компонента та useEffect) ...

    return (
        <div className="p-4">
            <h2 className="text-xl font-semibold mb-4">Прив’язка IoT-пристроїв до користувачів</h2>

            {/* Блок для прив'язки - тут потрібні окремі списки users та devices */}
            <div className="mb-4 grid grid-cols-3 gap-2">
                {/* Вибір користувача для нової прив'язки (використовує state users) */}
                <select
                    value={newAssociation.user_id}
                    onChange={(e) => setNewAssociation({ ...newAssociation, user_id: e.target.value })}
                    className="border p-2"
                >
                    <option value="">Оберіть користувача</option>
                    {/* Тут users.email коректно, бо це зі списку users */}
                    {users.map(user => (
                        <option key={user.id} value={user.id}>{user.email}</option>
                    ))}
                </select>

                {/* Вибір пристрою для нової прив'язки (використовує state devices) */}
                <select
                    value={newAssociation.iot_device_id}
                    onChange={(e) => setNewAssociation({ ...newAssociation, iot_device_id: e.target.value })}
                    className="border p-2"
                >
                    <option value="">Оберіть пристрій</option>
                    {/* Тут залежить від того, що повертає /iot-devices */}
                    {/* Якщо він повертає device_number, використовуйте device.device_number */}
                    {/* Якщо там є name або serial, використовуйте їх, як у вас */}
                    {/* Припускаємо, що /iot-devices повертає device_number або serial */}
                    {devices.map(device => (
                        <option key={device.id} value={device.id}>{device.device_number || device.serial || device.name || '—'}</option> // Адаптуйте поле відповідно до /iot-devices
                    ))}
                </select>

                <button onClick={handleBind} className="bg-blue-500 text-white p-2 rounded">
                    Прив’язати
                </button>
            </div>

            {/* Таблиця існуючих асоціацій - використовує дані, ВКЛЮЧЕНІ в об'єкт асоціації */}
            <table className="w-full border">
                <thead>
                <tr className="bg-gray-200">
                    <th className="border p-2">ID зв'язку</th> {/* Змінено назву */}
                    <th className="border p-2">Користувач (Email)</th> {/* Змінено назву */}
                    <th className="border p-2">Пристрій (Номер)</th> {/* Змінено назву */}
                    <th className="border p-2">Дія</th>
                </tr>
                </thead>
                <tbody>
                {associations.map((assoc) => (
                    // Ключ краще робити за ID самого запису зв'язку, якщо він є і унікальний
                    // Якщо ID зв'язку не унікальний або його немає, композитний ключ okay
                    <tr key={assoc.id || `${assoc.user_id}-${assoc.iot_device_id}`}>
                        <td className="border p-2">{assoc.id}</td> {/* Використовуємо ID зв'язку */}
                        {/* *** ВИПРАВЛЕНО: Використовуємо включені дані assoc.User *** */}
                        <td className="border p-2">{assoc.User?.email || '—'}</td>
                        {/* *** ВИПРАВЛЕНО: Використовуємо включені дані assoc.IotDevice *** */}
                        {/* Пам'ятайте, поле називається device_number */}
                        <td className="border p-2">{assoc.IotDevice?.device_number || '—'}</td>
                        <td className="border p-2">
                            <button
                                // Для відв'язки використовуємо user_id та iot_device_id із запису зв'язку
                                onClick={() => handleUnbind(assoc)}
                                className="bg-red-500 text-white p-1 px-2 rounded"
                            >
                                Відв’язати
                            </button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>

            {/* Опціонально: відображення сирих даних для налагодження */}
            {/* <pre>{JSON.stringify(associations, null, 2)}</pre> */}

        </div>
    );
};

export default AdminUserDevices;
