import { useState, useRef, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import UserMenu from "./UserMenu";

export default function Navbar({ setIsCreatingField }) {
    const [filter, setFilter] = useState("all");
    const [sort, setSort] = useState("asc");
    const [sortBy, setSortBy] = useState("name");
    const [isFilterMenuOpen, setIsFilterMenuOpen] = useState(false);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);

    const filterRef = useRef(null);
    const dropdownRef = useRef(null);
    const navigate = useNavigate();

    const handleAddField = () => {
        navigate('/create-field');
    };

    const handleSortToggle = () => {
        setSort((prev) => (prev === "asc" ? "desc" : "asc"));
    };

    const handleSortByChange = (newSortBy) => {
        setSortBy(newSortBy);
        setSort("asc");
        setIsDropdownOpen(false); // Закриваємо меню після вибору
    };

    const handleFilterChange = (newFilter) => {
        setFilter(newFilter);
        setIsFilterMenuOpen(false); // Закриваємо меню після вибору
    };

    const toggleFilterMenu = () => {
        setIsFilterMenuOpen((prev) => !prev);
    };

    const toggleDropdown = () => {
        setIsDropdownOpen((prev) => !prev);
    };

    // Закриття при кліку поза меню
    useEffect(() => {
        const handleClickOutside = (e) => {
            if (
                filterRef.current &&
                !filterRef.current.contains(e.target)
            ) {
                setIsFilterMenuOpen(false);
            }

            if (
                dropdownRef.current &&
                !dropdownRef.current.contains(e.target)
            ) {
                setIsDropdownOpen(false);
            }
        };

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, []);

    return (
        <div className="h-40 lg:h-20 w-full flex flex-wrap">
            <nav className="bg-gray-200 w-full lg:max-w-sm flex items-center border-b border-gray-300 order-2 lg:order-1">
                <div className="flex w-full p-2 gap-2 items-center">
                    <button
                        onClick={handleAddField}
                        className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-full font-semibold"
                    >
                        <i className="fas fa-plus mr-1" />
                        Додати поле
                    </button>

                    <div className="flex items-center gap-2 relative" ref={dropdownRef}>
                        <div className="relative flex items-center gap-1">
                            <button
                                onClick={toggleDropdown}
                                className="bg-gray-300 hover:bg-gray-400 text-gray-700 px-2 py-2 rounded-full"
                            >
                                {sortBy === "name" && "Назва"}
                                {sortBy === "size" && "Розмір"}
                                {sortBy === "date" && "Дата створення"}
                            </button>

                            <button
                                onClick={handleSortToggle}
                                className="bg-gray-300 hover:bg-gray-400 text-gray-700 px-2 py-2 rounded-full"
                            >
                                {sort === "asc" ? (
                                    <i className="fas fa-arrow-down" />
                                ) : (
                                    <i className="fas fa-arrow-up" />
                                )}
                            </button>
                        </div>

                        {isDropdownOpen && (
                            <div className="absolute bg-white shadow-md rounded-lg mt-1 p-2 w-48 border top-10 z-50">
                                <ul>
                                    <li>
                                        <button
                                            onClick={() => handleSortByChange("name")}
                                            className="w-full text-left px-4 py-2 hover:bg-gray-200"
                                        >
                                            Назва
                                        </button>
                                    </li>
                                    <li>
                                        <button
                                            onClick={() => handleSortByChange("size")}
                                            className="w-full text-left px-4 py-2 hover:bg-gray-200"
                                        >
                                            Розмір
                                        </button>
                                    </li>
                                    <li>
                                        <button
                                            onClick={() => handleSortByChange("date")}
                                            className="w-full text-left px-4 py-2 hover:bg-gray-200"
                                        >
                                            Дата створення
                                        </button>
                                    </li>
                                </ul>
                            </div>
                        )}
                    </div>

                    <div className="relative" ref={filterRef}>
                        <button
                            onClick={toggleFilterMenu}
                            className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-2 rounded-full "
                            title="Фільтрувати"
                        >
                            <i className="fas fa-filter" />
                        </button>

                        {isFilterMenuOpen && (
                            <div className="absolute bg-white shadow-md rounded-lg mt-2 p-2 w-48 border top-10 z-50">
                                <ul>
                                    <li>
                                        <button
                                            onClick={() => handleFilterChange("all")}
                                            className="w-full text-left px-4 py-2 hover:bg-gray-200"
                                        >
                                            Усі
                                        </button>
                                    </li>
                                    <li>
                                        <button
                                            onClick={() => handleFilterChange("active")}
                                            className="w-full text-left px-4 py-2 hover:bg-gray-200"
                                        >
                                            Активні
                                        </button>
                                    </li>
                                    <li>
                                        <button
                                            onClick={() => handleFilterChange("archived")}
                                            className="w-full text-left px-4 py-2 hover:bg-gray-200"
                                        >
                                            Архів
                                        </button>
                                    </li>
                                </ul>
                            </div>
                        )}
                    </div>
                </div>
            </nav>

            <nav className="bg-gray-100 w-auto flex-1 border-b border-gray-300 order-1 lg:order-2">
                <div className="flex h-full justify-between items-center">
                    <div className="relative w-full max-w-3xl px-6">
                        <div className="absolute h-10 mt-1 left-0 top-0 flex items-center pl-10">
                            <i className="fas fa-search text-gray-600" />
                        </div>
                        <input
                            type="search"
                            placeholder="Пошук поля..."
                            className="block w-full bg-gray-200 text-gray-700 font-bold rounded-full pl-12 pr-4 py-3 focus:outline-none focus:bg-white focus:shadow-md"
                        />
                    </div>
                    <UserMenu />
                </div>
            </nav>
        </div>
    );
}
