import { Navigate, Outlet } from "react-router-dom";

const PrivateRoute = () => {
    // Перевіряємо наявність токена в cookie
    const hasAuthToken = document.cookie.includes('auth_token=');
    console.log('Has auth token:', hasAuthToken);

    if (!hasAuthToken) {
        localStorage.clear(); // Чистимо локальні дані
        return <Navigate to="/login" replace />; // Переходимо на сторінку логіну
    }

    return <Outlet />; // Доступ до вкладених сторінок
};

export default PrivateRoute;
