import React from 'react';
import { useFieldContext } from '../FieldContext'; // імпортуємо useFieldContext
import DashboardContent from '../components/DashboardContent';
import MainContent from '../components/MainContent';
import CreateField from '../components/CreateField';


const HomePage = () => {
    const { isCreatingField, setIsCreatingField } = useFieldContext(); // використовуємо контекст

    return (
        <Layout>
            {isCreatingField ? (
                <CreateField />
            ) : (
                <MainContent />
            )}
        </Layout>
    );
};

export default HomePage;
