import React, { useEffect, useState } from 'react';
import api from '../utils/api';
import { useUserContext } from '../contexts/UserContext';
import {useTranslation} from "react-i18next"; // Для оновлення контексту
// Імпортуйте useNavigate, якщо ви його використовуєте для перенаправлення при неаутентифікації
// import { useNavigate } from 'react-router-dom';


const Profile = () => {
    // Отримуємо користувача з контексту для початкової перевірки та можливого початкового стану
    const { user: userFromContext, handleLoginSuccess } = useUserContext();
    const { t, i18n } = useTranslation();

    const [user, setUser] = useState(null);

    // Локальні стани для полів форми
    const [name, setName] = useState("");
    const [email, setEmail] = useState(""); // Ймовірно, email не редагується

    const [hasPassword, setHasPassword] = useState(false); // Чи є пароль для зміни

    const [profilePicture, setProfilePicture] = useState(null);

    // Локальні стани для полів зміни пароля
    const [currentPassword, setCurrentPassword] = useState("");
    const [newPassword, setNewPasswordState] = useState("");
    const [confirmPassword, setConfirmPasswordState] = useState("");

    const [message, setMessage] = useState(""); // Повідомлення про статус (успіх/помилка)



    useEffect(() => {
        const fetchUser = async () => {
            try {
                console.log("Profile: Fetching user data for profile page...");
                // Викликає ендпоінт /users/me, який має повернути ПОВНИЙ об'єкт користувача
                const res = await api.get('/users/me'); // Використовуйте шлях відносно baseURL з api.js
                const userData = res.data.user || res.data; // Адаптуйте, якщо дані не вкладені у .user

                console.log("Profile: Fetched user data:", userData);

                // Встановлює локальний стан користувача цими даними
                setUser(userData);
                setName(userData.name || ""); // Fallback для безпеки
                setEmail(userData.email || ""); // Fallback для безпеки

                // Перевіряємо, чи користувач має пароль (для відображення секції зміни пароля)
                setHasPassword(!!userData.password); // !! перетворює в булеве (true, якщо password не null/undefined/порожній рядок/0)


            } catch (error) {
                console.error("Profile: Помилка завантаження профілю:", error.response?.data?.message || error.message);
                setMessage(error.response?.data?.message || "Помилка завантаження профілю.");
                setUser(null); // Скидаємо user стан при помилці завантаження
            }
        };

        fetchUser();


    }, []); // Порожній масив залежностей - ефект виконується один раз при монтуванні


    // --- Обробник зміни файлу (обрано файл для завантаження) ---
    const handleFileSelect = (e) => {
        const file = e.target.files ? e.target.files[0] : null;
        setProfilePicture(file);
        setMessage(""); // Очищаємо повідомлення
        console.log("Profile: File selected for upload:", file);
    }


    // --- Обробник збереження змін профілю ---
    const handleSave = async (e) => {
        e.preventDefault();

        // Перевіряємо, що дані користувача завантажено локально перед збереженням
        if (!user || !user.id) {
            setMessage("Помилка: Дані користувача не завантажено. Спробуйте оновити сторінку.");
            return;
        }

        if (!validateForm()) return; // Валідуємо форму
        setMessage("Збереження..."); // Статус: збереження

        try {
            const formData = new FormData();

            if (name !== user.name) formData.append("name", name); // Додаємо ім'я, якщо змінилося

            if (profilePicture) {
                console.log("Profile: Appending file to FormData with key 'profilePictureFile'.");
                formData.append("profile_picture", profilePicture); // Використовуємо ім'я поля з бекенду: 'profile_picture'
            }

            // Додаємо поля пароля, якщо вони заповнені (поточний пароль потрібен для зміни)
            if (currentPassword) formData.append("currentPassword", currentPassword);
            if (newPassword) formData.append("newPassword", newPassword);


            console.log(`Profile: Sending update request for user ${user.id} with FormData.`);
            // Відправляємо запит PUT до бекенду з FormData
            const res = await api.put(`/users/${user.id}`, formData, {
                headers: { "Content-Type": "multipart/form-data" }, // Важливо для FormData
            });

            // Бекенд має повернути ОНОВЛЕНИЙ об'єкт користувача, включаючи новий profile_picture_url
            const updatedUserData = res.data.user || res.data; // Адаптуйте, якщо дані не вкладені у .user
            console.log("Profile: User updated successfully. Received updated data:", updatedUserData);

            // Оновлюємо ЛОКАЛЬНИЙ стан користувача свіжими даними з бекенду
            setUser(updatedUserData);
            // Оновлюємо локальні стани полів форми, якщо вони могли бути змінені на бекенді (напр., ім'я)
            setName(updatedUserData.name || "");
            // setEmail(updatedUserData.email || ""); // Якщо email можна змінювати

            setMessage("Зміни успішно збережено.");

            if (handleLoginSuccess) { // Перевіряємо, чи handleLoginSuccess доступна
                console.log("Profile: Calling handleLoginSuccess to update UserContext with updated data.");
                // handleLoginSuccess оновлює стан контексту та localStorage
                handleLoginSuccess(updatedUserData);
            } else {
                console.warn("Profile: handleLoginSuccess not available in context. UserMenu might not update.");

            }


            // Очищаємо поля пароля та стан обраного файлу після успішного збереження
            setCurrentPassword("");
            setNewPasswordState("");
            setConfirmPasswordState("");
            setProfilePicture(null); // Скидаємо обраний файл


        } catch (err) {
            console.error("Profile: Error saving profile:", err.response?.data?.message || err.message);
            setMessage(err.response?.data?.message || "Помилка збереження змін.");
        }
    };


    // --- Обробники змін для полів форми ---
    const handleNameChange = (e) => { setName(e.target.value); setMessage(""); }
    const handleCurrentPasswordChange = (e) => { setCurrentPassword(e.target.value); setMessage(""); }
    const handleNewPasswordChange = (e) => { setNewPasswordState(e.target.value); setMessage(""); }
    const handleConfirmPasswordChange = (e) => { setConfirmPasswordState(e.target.value); setMessage(""); }
    // handleFileSelect визначено вище


    // Якщо user ще не завантажений, показуємо індикатор
    if (!user) {
        // TODO: Можна додати перевірку loadingInitial з контексту

        return <p>Завантаження даних профілю...</p>;
    }
    // --- Функція валідації форми ---
    const validateForm = () => {
        setMessage(""); // Очищаємо попередні повідомлення про помилки

        // Валідація паролів, якщо вони змінюються
        if ((newPassword || confirmPassword) && !currentPassword) {
            setMessage("Будь ласка, введіть поточний пароль для зміни пароля.");
            return false;
        }

        if (newPassword && newPassword !== confirmPassword) {
            setMessage("Новий пароль та підтвердження пароля не співпадають.");
            return false;
        }

        // TODO: Додати інші правила валідації за необхідності (наприклад, мінімальна довжина пароля, формат email, розмір файлу)

        // Якщо все добре
        return true;
    };
    // Якщо user завантажений, рендеримо форму
    return (
        <div className="w-full md:w-2/3 p-6">
            <h2 className="text-2xl font-semibold mb-4">{t('my_acc')}</h2>

            <form onSubmit={handleSave} className="space-y-4" encType="multipart/form-data"> {/* encType обов'язковий для FormData з файлами */}
                <div>
                    <label className="block font-medium text-gray-700 mb-1">Фото профілю</label>
                    {/* Input для вибору файлу */}
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleFileSelect}
                        className="w-full"
                    />

                    {profilePicture ? (
                        // Якщо обрано новий файл - показуємо його попередній перегляд
                        <div className="mt-2">
                            <p className="text-sm text-gray-600">Попередній перегляд нового фото:</p>
                            <img
                                src={URL.createObjectURL(profilePicture)}
                                alt="Попередній перегляд нового фото"
                                className="w-24 h-24 rounded-full object-cover border border-blue-500"
                            />
                            {/* TODO: Додати useEffect Cleanup для відкликання ObjectURL */}
                        </div>
                    ) : (
                        // Якщо новий файл НЕ обрано, але є URL поточного фото в стані user - показуємо його
                        user.profile_picture_url && (
                            <div className="mt-2">
                                <p className="text-sm text-gray-600">Поточне фото:</p>
                                <img

                                    src={`http://localhost:5000${user.profile_picture_url}`}
                                    alt={user?.name ? `Поточне фото профілю ${user.name}` : 'Поточне фото профілю'}
                                    className="w-24 h-24 rounded-full object-cover border border-gray-300"
                                />
                                {/* TODO: Додати кнопку "Видалити фото профілю" */}
                            </div>
                        )
                    )}



                </div>

                {/* Секція Email (якщо не редагується) */}
                <div>
                    <label className="block font-medium text-gray-700 mb-1">Email</label>
                    <input
                        type="email"
                        value={email} // Використовуємо email з локального стану
                        disabled // Припускаємо, що email не редагується
                        className="w-full px-4 py-2 border border-gray-300 rounded bg-gray-100 cursor-not-allowed"
                    />
                </div>

                {/* Секція Ім'я */}
                <div>
                    <label className="block font-medium text-gray-700 mb-1">Ім’я</label>
                    <input
                        type="text"
                        value={name} // Використовуємо ім'я з локального стану
                        onChange={handleNameChange} // Використовуємо обробник зміни
                        className="w-full px-4 py-2 border border-gray-300 rounded"
                    />
                </div>

                {/* Секція зміни пароля (відображаємо, якщо user має пароль) */}
                {/* Перевіряємо наявність поля 'password' у об'єкті користувача, отриманому з бекенду */}
                {user.password !== undefined && user.password !== null ? (
                    <>
                        <hr className="my-4" />
                        <h3 className="text-lg font-semibold">Зміна пароля</h3>

                        <div>
                            <label className="block font-medium text-gray-700 mb-1">Поточний пароль</label>
                            <input
                                type="password"
                                value={currentPassword}
                                onChange={handleCurrentPasswordChange}
                                className="w-full px-4 py-2 border border-gray-300 rounded"
                            />
                        </div>

                        <div>
                            <label className="block font-medium text-gray-700 mb-1">Новий пароль</label>
                            <input
                                type="password"
                                value={newPassword}
                                onChange={handleNewPasswordChange}
                                className="w-full px-4 py-2 border border-gray-300 rounded"
                            />
                        </div>

                        <div>
                            <label className="block font-medium text-gray-700 mb-1">Підтвердження пароля</label>
                            <input
                                type="password"
                                value={confirmPassword}
                                onChange={handleConfirmPasswordChange}
                                className="w-full px-4 py-2 border border-gray-300 rounded"
                            />
                        </div>
                    </>
                ) : (
                    // Опціонально: Показати секцію для встановлення пароля, якщо його немає
                    <div className="mt-4 text-gray-600">
                        <p>Ви увійшли без пароля. Ви можете додати пароль у налаштуваннях.</p> {/* TODO: Реалізувати функціонал додавання пароля */}
                    </div>
                )}


                {message && <p className="text-sm text-blue-600">{message}</p>}

                <button
                    type="submit"
                    className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700 transition"
                >
                    Зберегти зміни
                </button>
            </form>
        </div>
    );
};

export default Profile;
