// src/pages/HomePage.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import DashboardContent from '../components/DashboardContent';
import MainContent from '../components/MainContent.jsx';

const HomePage = () => {
    const navigate = useNavigate();

    const handleFieldSelectForHome = (fieldId) => {
        console.log('HomePage: handleFieldSelectForHome called with ID:', fieldId);
        console.log(`HomePage: Attempting navigation to /field/${fieldId}`);
        navigate(`/field/${fieldId}`);
    };

    return (
        <div className="flex flex-wrap">
            {/* !!! Переконайтеся, що цей рядок виглядає САМЕ ТАК !!! */}
            <DashboardContent
                onFieldSelect={handleFieldSelectForHome} // <<-- Передаємо пропс onFieldSelect
                selectedFieldId={null} // На домашній сторінці не виділяємо
            />
            <MainContent />
        </div>
    );
}

export default HomePage;
