// src/components/AnalyticsContent.js
import React, { useEffect, useState } from 'react'; // Імпортуємо useState
import api from '../utils/api';
// useFieldContext більше не потрібен тут

// Приймаємо пропс selectedFieldId
const AnalyticsContent = ({ selectedFieldId }) => {
    // ✅ Оголошення станів (були відсутні, що викликало помилки ESLint)
    const [timeRange, setTimeRange] = useState('7d'); // Стан для діапазону часу
    const [chartData, setChartData] = useState(null); // Стан для даних графіка
    const [isLoading, setIsLoading] = useState(false); // Стан для індикатора завантаження
    const [error, setError] = useState(null); // Стан для помилки

    // ✅ Функція для зміни діапазону часу (була відсутня, що викликало помилку ESLint)
    const handleTimeRangeChange = (newTimeRange) => {
        setTimeRange(newTimeRange);
    };

    // Ефект для завантаження даних при зміні selectedFieldId або timeRange
    useEffect(() => {
        if (!selectedFieldId) {
            // Використовуємо оголошені стани
            setChartData(null);
            setError(null);
            setIsLoading(false);
            return;
        }

        // ТУТ МОЖНА ДОДАТИ selectField запит, якщо це потрібно робити саме при відображенні аналітики
        // (Ваш закоментований код залишається як приклад, якщо знадобиться)
        // ...

        // ✅ Використовуємо оголошені стани та timeRange
        setIsLoading(true);
        setError(null);
        // Виправлено помилку з математичними символами у рядку URL
        const apiUrl = `/field-measurements/chart/${selectedFieldId}/${timeRange}`;


        api.get(apiUrl)
            .then(response => {
                // Використовуємо оголошені стани
                setChartData(response.data);
                setIsLoading(false);
            })
            .catch(err => {
                console.error('Error fetching chart data:', err);
                // Використовуємо оголошені стани
                setError('Не вдалося завантажити дані графіка.');
                setChartData(null);
                setIsLoading(false);
            });

        // ТУТ ТАКОЖ МОЖНА ДОДАТИ deselectField У Cleanup функцію, якщо select робиться тут
        // ... (Ваш закоментований код залишається як приклад, якщо знадобиться)

    }, [selectedFieldId, timeRange]); // Залежності ефекту

    // ✅ Використовуємо оголошені стани (chartData, isLoading, error) та функцію (handleTimeRangeChange) у JSX
    return (
        <div id="main-content" className="w-full flex-1">
            <div className="flex flex-1 flex-wrap">
                <div className="w-full xl:w-2/3 p-6 xl:max-w-6xl">
                    <div className="max-w-full lg:max-w-3xl xl:max-w-5xl">
                        <div className="border-b p-3">
                            <h5 className="font-bold text-black">Графік вимірювань {selectedFieldId ? `для поля ID: ${selectedFieldId}` : ''}</h5>
                        </div>
                        <div className="p-5">
                            {/* Кнопки вибору масштабування */}
                            <div className="flex space-x-4 mb-4">
                                <button
                                    onClick={() => handleTimeRangeChange('7d')} // ✅ Використовуємо handleTimeRangeChange
                                    className={`px-4 py-2 rounded ${timeRange === '7d' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`} // ✅ Використовуємо timeRange
                                >
                                    Тиждень (7д)
                                </button>
                                <button
                                    onClick={() => handleTimeRangeChange('6m')} // ✅ Використовуємо handleTimeRangeChange
                                    className={`px-4 py-2 rounded ${timeRange === '6m' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`} // ✅ Використовуємо timeRange
                                >
                                    6 місяців (6м)
                                </button>
                                <button
                                    onClick={() => handleTimeRangeChange('1y')} // ✅ Використовуємо handleTimeRangeChange
                                    className={`px-4 py-2 rounded ${timeRange === '1y' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`} // ✅ Використовуємо timeRange
                                >
                                    Рік (1р)
                                </button>
                            </div>

                            {/* Область для відображення графіка */}
                            <div className="ct-chart ct-golden-section" id="chart1">
                                {!selectedFieldId && (
                                    <p className="text-center text-gray-500">Будь ласка, оберіть поле зі списку ліворуч, щоб побачити графік.</p>
                                )}
                                {selectedFieldId && isLoading && ( // ✅ Використовуємо isLoading
                                    <p className="text-center text-gray-500">Завантаження даних графіка...</p>
                                )}
                                {selectedFieldId && error && ( // ✅ Використовуємо error
                                    <p className="text-center text-red-500">{error}</p> // ✅ Використовуємо error
                                )}
                                {selectedFieldId && !isLoading && !error && !chartData && ( // ✅ Використовуємо isLoading, error, chartData
                                    <p className="text-center text-gray-500">Дані для обраного поля за цей період не знайдені.</p>
                                )}
                            </div>
                        </div>

                        {/* Розділ таблиці (залишаємо як було) */}
                        <div className="p-3">
                            <div className="border-b p-3">
                                <h5 className="font-bold text-black">Table</h5>
                            </div>
                            <div className="p-5">
                                <table className="w-full p-5 text-gray-700">
                                    <thead>
                                    <tr>
                                        <th className="text-left text-blue-900">Name</th>
                                        <th className="text-left text-blue-900">Side</th>
                                        <th className="text-left text-blue-900">Role</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    <tr>
                                        <td>Obi Wan Kenobi</td>
                                        <td>Light</td>
                                        <td>Jedi</td>
                                    </tr>
                                    <tr>
                                        <td>Greedo</td>
                                        <td>South</td>
                                        <td>Scumbag</td>
                                    </tr>
                                    <tr>
                                        <td>Darth Vader</td>
                                        <td>Dark</td>
                                        <td>Sith</td>
                                    </tr>
                                    </tbody>
                                </table>
                                <p className="py-2"><a href="#">See More issues...</a></p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default AnalyticsContent;
