import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import api from "../utils/api"; // шляхи підлаштуй під структуру проєкту

const CreateField = () => {
    const navigate = useNavigate();

    const [name, setName] = useState('');
    const [area, setArea] = useState('');
    const [geoZone, setGeoZone] = useState(null); // очікується масив координат: [[lng, lat], ...]

    const handleCancel = () => {
        navigate(-1);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!geoZone) {
            alert("Будь ласка, позначте поле на карті.");
            return;
        }

        const payload = {
            name,
            area: parseFloat(area),
            geo_zone: {
                type: "Polygon",
                coordinates: [geoZone] // важливо: обгортаємо координати у вкладений масив
            }
        };

        try {
            const res = await api.post('/fields', payload);
            alert("Поле успішно створене!");
            navigate("/dashboard"); // або куди потрібно
        } catch (error) {
            console.error("Помилка при створенні поля:", error);
            alert(error?.response?.data?.error || "Щось пішло не так");
        }
    };

    return (
        <div className="h-screen w-full flex-1">
            <div className="p-6">
                <h5 className="font-bold text-black text-xl mb-4">Створити нове поле</h5>
                <form onSubmit={handleSubmit}>
                    <label className="block mb-4">
                        Назва поля:
                        <input
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            className="w-full border border-gray-300 p-2 rounded-lg"
                            required
                        />
                    </label>
                    <label className="block mb-4">
                        Площа поля (в га):
                        <input
                            type="number"
                            value={area}
                            onChange={(e) => setArea(e.target.value)}
                            className="w-full border border-gray-300 p-2 rounded-lg"
                            required
                            step="0.01"
                        />
                    </label>

                    {/* ⬇️ Тут буде карта з полігоном */}
                    <div className="my-6">
                        <h6 className="font-semibold mb-2">Позначте поле на карті:</h6>
                        {/* Пізніше додамо компонент з Google Maps */}
                        {/* Передавати setGeoZone([...]) */}
                        <div className="h-80 border rounded-lg flex items-center justify-center text-gray-500">
                            Карта незабаром тут 🗺️
                        </div>
                    </div>

                    <button
                        type="submit"
                        className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-full"
                    >
                        Створити поле
                    </button>
                </form>

                <button
                    onClick={handleCancel}
                    className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-full font-semibold mt-4"
                >
                    Скасувати
                </button>
            </div>
        </div>
    );
};

export default CreateField;
