import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import api from "../utils/api"; // —à–ª—è—Ö–∏ –ø—ñ–¥–ª–∞—à—Ç—É–π –ø—ñ–¥ —Å—Ç—Ä—É–∫—Ç—É—Ä—É –ø—Ä–æ—î–∫—Ç—É

const CreateField = () => {
    const navigate = useNavigate();

    const [name, setName] = useState('');
    const [area, setArea] = useState('');
    const [geoZone, setGeoZone] = useState(null); // –æ—á—ñ–∫—É—î—Ç—å—Å—è –º–∞—Å–∏–≤ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç: [[lng, lat], ...]

    const handleCancel = () => {
        navigate(-1);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!geoZone) {
            alert("–ë—É–¥—å –ª–∞—Å–∫–∞, –ø–æ–∑–Ω–∞—á—Ç–µ –ø–æ–ª–µ –Ω–∞ –∫–∞—Ä—Ç—ñ.");
            return;
        }

        const payload = {
            name,
            area: parseFloat(area),
            geo_zone: {
                type: "Polygon",
                coordinates: [geoZone] // –≤–∞–∂–ª–∏–≤–æ: –æ–±–≥–æ—Ä—Ç–∞—î–º–æ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç–∏ —É –≤–∫–ª–∞–¥–µ–Ω–∏–π –º–∞—Å–∏–≤
            }
        };

        try {
            const res = await api.post('/fields', payload);
            alert("–ü–æ–ª–µ —É—Å–ø—ñ—à–Ω–æ —Å—Ç–≤–æ—Ä–µ–Ω–µ!");
            navigate("/dashboard"); // –∞–±–æ –∫—É–¥–∏ –ø–æ—Ç—Ä—ñ–±–Ω–æ
        } catch (error) {
            console.error("–ü–æ–º–∏–ª–∫–∞ –ø—Ä–∏ —Å—Ç–≤–æ—Ä–µ–Ω–Ω—ñ –ø–æ–ª—è:", error);
            alert(error?.response?.data?.error || "–©–æ—Å—å –ø—ñ—à–ª–æ –Ω–µ —Ç–∞–∫");
        }
    };

    return (
        <div className="h-screen w-full flex-1">
            <div className="p-6">
                <h5 className="font-bold text-black text-xl mb-4">–°—Ç–≤–æ—Ä–∏—Ç–∏ –Ω–æ–≤–µ –ø–æ–ª–µ</h5>
                <form onSubmit={handleSubmit}>
                    <label className="block mb-4">
                        –ù–∞–∑–≤–∞ –ø–æ–ª—è:
                        <input
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            className="w-full border border-gray-300 p-2 rounded-lg"
                            required
                        />
                    </label>
                    <label className="block mb-4">
                        –ü–ª–æ—â–∞ –ø–æ–ª—è (–≤ –≥–∞):
                        <input
                            type="number"
                            value={area}
                            onChange={(e) => setArea(e.target.value)}
                            className="w-full border border-gray-300 p-2 rounded-lg"
                            required
                            step="0.01"
                        />
                    </label>

                    {/* ‚¨áÔ∏è –¢—É—Ç –±—É–¥–µ –∫–∞—Ä—Ç–∞ –∑ –ø–æ–ª—ñ–≥–æ–Ω–æ–º */}
                    <div className="my-6">
                        <h6 className="font-semibold mb-2">–ü–æ–∑–Ω–∞—á—Ç–µ –ø–æ–ª–µ –Ω–∞ –∫–∞—Ä—Ç—ñ:</h6>
                        {/* –ü—ñ–∑–Ω—ñ—à–µ –¥–æ–¥–∞–º–æ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –∑ Google Maps */}
                        {/* –ü–µ—Ä–µ–¥–∞–≤–∞—Ç–∏ setGeoZone([...]) */}
                        <div className="h-80 border rounded-lg flex items-center justify-center text-gray-500">
                            –ö–∞—Ä—Ç–∞ –Ω–µ–∑–∞–±–∞—Ä–æ–º —Ç—É—Ç üó∫Ô∏è
                        </div>
                    </div>

                    <button
                        type="submit"
                        className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-full"
                    >
                        –°—Ç–≤–æ—Ä–∏—Ç–∏ –ø–æ–ª–µ
                    </button>
                </form>

                <button
                    onClick={handleCancel}
                    className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-full font-semibold mt-4"
                >
                    –°–∫–∞—Å—É–≤–∞—Ç–∏
                </button>
            </div>
        </div>
    );
};

export default CreateField;
