import React, { useEffect, useState } from 'react';
import { useParams } from "react-router-dom";
import api from '../utils/api';

const Field = () => {
    const { id } = useParams();
    const [field, setField] = useState(null);

    useEffect(() => {
        api.get(`/fields/${id}`)
            .then(res => setField(res.data))
            .catch(err => console.error(err));
    }, [id]);

    if (!field) return <div className="p-4">Завантаження...</div>;

    return (
        <div className=" p-6 w-full flex-1">
            <h1 className="text-3xl font-bold mb-2">{field.name}</h1>
            <p className="text-gray-600 mb-4">{field.description}</p>

            <div className="grid grid-cols-2 gap-4">
                <p><strong>Площа:</strong> {field.area} га</p>
                <p><strong>Тип ґрунту:</strong> {field.soilType}</p>
                <p><strong>Створено:</strong> {new Date(field.createdAt).toLocaleDateString()}</p>
                <p><strong>Оновлено:</strong> {new Date(field.updatedAt).toLocaleDateString()}</p>
            </div>

            <hr className="my-6" />

            <h2 className="text-xl font-semibold mb-2">Останнє вимірювання</h2>
            {field.lastMeasurement ? (
                <ul className="list-disc ml-6 text-gray-700">
                    <li>Температура: {field.lastMeasurement.temperature} °C</li>
                    <li>Вологість: {field.lastMeasurement.humidity} %</li>
                    <li>Кислотність: {field.lastMeasurement.ph}</li>
                    <li>Дата: {new Date(field.lastMeasurement.date).toLocaleString()}</li>
                </ul>
            ) : (
                <p className="text-gray-500">Немає вимірювань</p>
            )}
        </div>
    );
};

export default Field;
