const Sidebar = () => (
    <div id="sidebar" className="h-screen w-16 bg-white text-white px-4 flex items-center fixed shadow">
        <ul className="list-reset">
            {[
                { icon: "home", text: "Home" },
                { icon: "tasks", text: "Tasks" },
                { icon: "envelope", text: "Messages" },
                { icon: "chart-area", text: "Analytics", active: true },
                { icon: "wallet", text: "Payments" },
            ].map(({ icon, text, active }) => (
                <li key={text} className="my-2">
                    <a
                        href="#"
                        className={`block py-3 pl-1 align-middle text-gray-600 no-underline hover:text-indigo-400 ${
                            active ? "text-indigo-400" : ""
                        }`}
                    >
                        <i className={`fas fa-${icon} fa-fw mr-3`}></i>
                        <span className="inline-block text-sm">{text}</span>
                    </a>
                </li>
            ))}
        </ul>
    </div>
);
