import React, { useState } from "react";

const Profile = () => {
    const [name, setName] = useState("John Doe"); // Можна замінити на отримане з бекенду
    const [email] = useState("johndoe@example.com"); // тільки для відображення
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();

        if (password && password !== confirmPassword) {
            alert("Паролі не співпадають!");
            return;
        }

        // Тут буде відправка даних на бекенд
        console.log("Updated name:", name);
        if (password) {
            console.log("Updated password:", password);
        }

        alert("Профіль оновлено!");
    };

    return (
        <div className="p-6 w-full md:w-2/3">
            <h2 className="text-2xl font-bold mb-4">Мій акаунт</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block font-medium text-gray-700">Email</label>
                    <input
                        type="email"
                        value={email}
                        disabled
                        className="mt-1 block w-full border-gray-300 rounded-md shadow-sm bg-gray-100"
                    />
                </div>

                <div>
                    <label className="block font-medium text-gray-700">Ім'я</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        className="mt-1 block w-full border-gray-300 rounded-md shadow-sm"
                    />
                </div>

                <div>
                    <label className="block font-medium text-gray-700">Новий пароль</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        className="mt-1 block w-full border-gray-300 rounded-md shadow-sm"
                        placeholder="Залиште порожнім, якщо не змінюєте"
                    />
                </div>

                <div>
                    <label className="block font-medium text-gray-700">Підтвердження паролю</label>
                    <input
                        type="password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        className="mt-1 block w-full border-gray-300 rounded-md shadow-sm"
                        placeholder="Підтвердіть новий пароль"
                    />
                </div>

                <button
                    type="submit"
                    className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
                >
                    Зберегти зміни
                </button>
            </form>
        </div>
    );
};

export default Profile;
