// src/components/AnalyticsContent.js
import React, { useEffect, useState, useRef } from 'react'; // Import useRef
// ✅ Виправлено імпорт Chartist: імпортуємо іменовані експорти
import { LineChart, Svg } from 'chartist'; // Імпортуємо LineChart для лінійного графіка та Svg для анімацій
// ✅ Виправлено шлях імпорту CSS - спробуємо стандартний шлях
import 'chartist/dist/index.css'; // Імпорт CSS Chartist


import api from '../utils/api';
// useFieldContext більше не потрібен тут

const AnalyticsContent = ({ selectedFieldId }) => {
    const [timeRange, setTimeRange] = useState('7d'); // Стан для діапазону часу
    const [chartData, setChartData] = useState(null); // Стан для даних графіка
    const [isLoading, setIsLoading] = useState(false); // Стан для індикатора завантаження
    const [error, setError] = useState(null); // Стан для помилки

    // ✅ Ref to the chart container element
    const chartContainerRef = useRef(null);
    // ✅ Ref to store the Chartist instance
    const chartInstanceRef = useRef(null);

    // Функції для backend select/deselect (як раніше модифіковано)
    // ID поля не передається в тілі запиту
    const selectField = async () => {
        try {
            console.log(`Відправка запиту на select поточного поля.`);
            await api.post('/fields/select/');
            console.log(`Поле успішно вибрано (визначено на сервері).`);
        } catch (err) {
            console.error(`Не вдалося вибрати поле на сервері:`, err);
        }
    };

    const deselectField = async () => {
        try {
            console.log(`Відправка запиту на deselect поточного поля.`);
            await api.post('/fields/deselect/');
            console.log(`Поле успішно деселектнуто (визначено на сервері).`);
        } catch (err) {
            console.error(`Не вдалося деселектнути поле на сервері:`, err);
        }
    };


    // Ефект для завантаження даних та керування станом "вибраності" поля
    useEffect(() => {
        if (!selectedFieldId) {
            setChartData(null);
            setError(null);
            setIsLoading(false);
            return;
        }

        selectField(); // Викликаємо selectField без аргументів

        setIsLoading(true);
        setError(null);
        const apiUrl = `/field-measurements/chart/${selectedFieldId}/${timeRange}`;

        api.get(apiUrl)
            .then(response => {
                // Припускаємо, що response.data є в Chartist сумісному форматі: { labels: [...], series: [...] }
                setChartData(response.data);
                setIsLoading(false);
            })
            .catch(err => {
                console.error('Error fetching chart data:', err);
                setError('Не вдалося завантажити дані графіка.');
                setChartData(null);
                setIsLoading(false);
            });

        // Cleanup function to deselect the field
        return () => {
            // При демонтажі компонента або зміні selectedFieldId, деселектимо поле
            deselectField(); // Викликаємо deselectField без аргументів
        };

    }, [selectedFieldId, timeRange]); // Залежності

    // ✅ Ефект для рендерингу графіка, коли chartData змінюється
    useEffect(() => {
        // Перевіряємо, чи є дані, чи є контейнер, і чи не завантажуємо/немає помилки
        if (chartData && chartContainerRef.current && !isLoading && !error) {
            // Знищуємо попередній екземпляр графіка, якщо він існує
            if (chartInstanceRef.current) {
                chartInstanceRef.current.detach();
            }

            // ✅ Визначаємо опції для Chartist (можна налаштувати)
            const options = {
                low: 0, // Початок осі Y від 0
                // showArea: true, // Показати область під лінією
                // showPoint: false, // Приховати точки на лінії
                fullWidth: true, // Використовувати всю ширину контейнера
                chartPadding: { // Додати відступи
                    right: 40
                },
                axisX: {
                    // Приклад форматування міток осі X, якщо labels - дати/час
                    labelOffset: {
                        x: 0,
                        y: 5
                    }
                    // labelInterpolationFnc: function(value, index) {
                    //    // Можна форматувати дати тут, наприклад, moment(value).format('DD.MM')
                    //    return value; // За замовчуванням просто повертає значення
                    // }
                },
                axisY: {
                    labelInterpolationFnc: function(value) {
                        // Можна додати одиниці виміру, якщо вони відомі (напр., value + ' %')
                        return value;
                    },
                    offset: 40 // Збільшити відступ для міток осі Y, якщо вони довгі
                }
            };

            // ✅ Створюємо новий екземпляр LineChart
            chartInstanceRef.current = new LineChart( // Використовуємо імпортований LineChart
                chartContainerRef.current, // Передаємо посилання на DOM-елемент
                chartData, // Передаємо отримані дані
                options // Передаємо опції
            );

            // Необов'язково: Повторно застосувати анімації, якщо потрібно (адаптуємо з вашого HTML)
            // Приклад анімації для ліній/областей
            chartInstanceRef.current.on('draw', function(data) {
                if (data.type === 'line' || data.type === 'area') {
                    data.element.animate({
                        d: {
                            begin: 1000 * data.index,
                            dur: 1000,
                            // ✅ Використовуємо Svg.Easing замість Chartist.Svg.Easing
                            from: data.path.clone().scale(1, 0).translate(0, data.chartRect.height()).stringify(),
                            to: data.path.clone().stringify(),
                            easing: Svg.Easing.easeOutQuint // Використовуємо імпортований Svg
                        }
                    });
                }
            });

            // ✅ Додано умову для знищення графіка, якщо дані стають порожніми/null
        } else if (
            (!chartData || (Array.isArray(chartData.series) && chartData.series.every(s => s.length === 0)) || (Array.isArray(chartData.labels) && chartData.labels.length === 0)) // Немає даних
            && chartInstanceRef.current // Існує екземпляр графіка
        ) {
            // Якщо дані порожні/null (існуючий графік), знищуємо його
            chartInstanceRef.current.detach();
            chartInstanceRef.current = null; // Очищаємо реф
        }

        // ✅ Cleanup функція для екземпляра графіка
        return () => {
            if (chartInstanceRef.current) {
                chartInstanceRef.current.detach(); // Знищуємо екземпляр графіка при демонтажі
                chartInstanceRef.current = null; // Очищаємо реф
            }
        };

    }, [chartData, isLoading, error]); // Залежності: перезапускаємо ефект при зміні даних, стану завантаження чи помилки


    // ✅ Функція для зміни діапазону часу (залишається без змін)
    const handleTimeRangeChange = (newTimeRange) => {
        setTimeRange(newTimeRange);
    };

    return (
        <div id="main-content" className="w-full flex-1">
            <div className="flex flex-1 flex-wrap">
                {/* Злегка відкоригована ширина */}
                <div className="w-full xl:w-2/3 p-6 xl:max-w-6xl">
                    {/* "Контейнер" для графіків*/}
                    <div className="max-w-full lg:max-w-3xl xl:max-w-5xl">

                        {/*Card для графіка*/}
                        <div className="border-b p-3">
                            {/* Відображаємо ID поля, якщо воно вибрано */}
                            <h5 className="font-bold text-black">Графік вимірювань {selectedFieldId ? `для поля ID: ${selectedFieldId}` : ''}</h5>
                            {/* Кнопки вибору масштабування */}
                            <div className="flex space-x-4 mt-2"> {/* Додано mt-2 для відступу */}
                                <button
                                    onClick={() => handleTimeRangeChange('7d')}
                                    className={`px-3 py-1 text-sm rounded ${timeRange === '7d' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    7 днів
                                </button>
                                <button
                                    onClick={() => handleTimeRangeChange('6m')}
                                    className={`px-3 py-1 text-sm rounded ${timeRange === '6m' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    6 місяців
                                </button>
                                <button
                                    onClick={() => handleTimeRangeChange('1y')}
                                    className={`px-3 py-1 text-sm rounded ${timeRange === '1y' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    1 рік
                                </button>
                                {/* Можна додати кнопку "Весь час", якщо потрібно */}
                            </div>
                        </div>
                        <div className="p-5">
                            {/* ✅ Призначаємо ref для div контейнера графіка */}
                            <div className="ct-chart ct-golden-section" id="chart1" ref={chartContainerRef}>
                                {/* ✅ Відображаємо повідомлення умовно. Графік буде намальовано тут Chartist-ом */}
                                {!selectedFieldId && (
                                    <p className="text-center text-gray-500">Будь ласка, оберіть поле зі списку ліворуч, щоб побачити графік.</p>
                                )}
                                {selectedFieldId && isLoading && (
                                    <p className="text-center text-gray-500">Завантаження даних графіка...</p>
                                )}
                                {selectedFieldId && error && (
                                    <p className="text-center text-red-500">{error}</p>
                                )}
                                {/* Показуємо повідомлення про відсутність даних, тільки якщо поле вибрано, не завантажується, немає помилок, і дані порожні/відсутні */}
                                {selectedFieldId && !isLoading && !error && (!chartData || (Array.isArray(chartData.series) && chartData.series.every(s => s.length === 0)) || (Array.isArray(chartData.labels) && chartData.labels.length === 0)) && (
                                    <p className="text-center text-gray-500">Дані для обраного поля за цей період не знайдені.</p>
                                )}
                                {/* Графік Chartist буде рендеритися *всередині* цього div */}
                            </div>
                        </div>
                        {/*/Card для графіка*/}

                        {/* Card для таблиці - залишаємо як є */}
                        {/* TODO: Можливо, зробити цю таблицю динамічною з даними обраного поля? */}
                        <div className="p-3">
                            <div className="border-b p-3">
                                <h5 className="font-bold text-black">Table</h5>
                            </div>
                            <div className="p-5">
                                <table className="w-full p-5 text-gray-700">
                                    <thead>
                                    <tr>
                                        <th className="text-left text-blue-900">Name</th>
                                        <th className="text-left text-blue-900">Side</th>
                                        <th className="text-left text-blue-900">Role</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    <tr>
                                        <td>Obi Wan Kenobi</td>
                                        <td>Light</td>
                                        <td>Jedi</td>
                                    </tr>
                                    <tr>
                                        <td>Greedo</td>
                                        <td>South</td>
                                        <td>Scumbag</td>
                                    </tr>
                                    <tr>
                                        <td>Darth Vader</td>
                                        <td>Dark</td>
                                        <td>Sith</td>
                                    </tr>
                                    </tbody>
                                </table>
                                <p className="py-2"><a href="#">See More issues...</a></p>
                            </div>
                        </div>
                        {/*/table Card*/}

                    </div>

                </div>

                {/* Друга колонка - залишаємо як є */}
                {/* TODO: Ці додаткові графіки (#chart2, #chart3, #chart4) - це жорстко закодовані приклади.
                         Якщо вам потрібні динамічні графіки для різних типів сенсорів або даних,
                         потрібно буде отримати більше даних і створити окремі екземпляри Chartist
                         або компонент з кількома графіками. Наразі це просто порожні div-и. */}
                <div className="w-full xl:w-1/3 p-6 xl:max-w-4xl border-l-1 border-gray-300">

                    {/* "Контейнер" для графіків*/}
                    <div className="max-w-sm lg:max-w-3xl xl:max-w-5xl">

                        {/*Card для графіка*/}
                        <div className="border-b p-3">
                            <h5 className="font-bold text-black">Graph 2</h5> {/* Названо інакше */}
                        </div>
                        <div className="p-5">
                            {/* ✅ Цим div-ам також потрібні будуть refs, якщо ви захочете в них малювати графіки */}
                            <div className="ct-chart ct-golden-section" id="chart2"></div>
                        </div>
                        {/*/Card для графіка*/}

                        {/*Card для графіка*/}
                        <div className="border-b p-3">
                            <h5 className="font-bold text-black">Graph 3</h5> {/* Названо інакше */}
                        </div>
                        <div className="p-5">
                            {/* ✅ Цим div-ам також потрібні будуть refs, якщо ви захочете в них малювати графіки */}
                            <div className="ct-chart ct-golden-section" id="chart3"></div>
                        </div>
                        {/*/Card для графіка*/}

                        {/*Card для графіка*/}
                        <div className="border-b p-3">
                            <h5 className="font-bold text-black">Graph 4</h5> {/* Названо інакше */}
                        </div>
                        <div className="p-5">
                            {/* ✅ Цим div-ам також потрібні будуть refs, якщо ви захочете в них малювати графіки */}
                            <div className="ct-chart ct-golden-section" id="chart4"></div>
                        </div>
                        {/*/Card для графіка*/}

                        {/*Template Card - залишаємо як є */}
                        <div className="p-3">
                            <div className="border-b p-3">
                                <h5 className="font-bold text-black">Template</h5>
                            </div>
                            <div className="p-5">

                            </div>
                        </div>
                        {/*/Template Card*/}

                    </div>

                </div>

            </div>

        </div>

    );
}

export default AnalyticsContent;
