import { Link, useLocation } from 'react-router-dom';
import { useContext } from 'react';
import { UserContext } from '../contexts/UserContext';

export default function Sidebar() {
    const { isAdmin } = useContext(UserContext);
    const location = useLocation();

    return (
        <div id="sidebar" className="h-screen w-16 menu bg-white text-white px-4 flex items-center nunito static fixed shadow">
            <ul className="list-reset">
                {[{ icon: 'fa-home', text: 'HomePage', path: '/home' },
                    { icon: 'fa-chart-area', text: 'AnalyticsPage', path: '/analytics' },
                    { icon: 'fa-envelope', text: 'Messages', path: '/messages' },
                    isAdmin && { icon: 'fa-tools', text: 'Admin Panel', path: '/admin' }
                ]
                    .filter(item => item) // Фільтруємо, щоб не було порожніх значень
                    .map(({ icon, text, path }, index) => {
                        const isActive = location.pathname.startsWith(path);

                        return (
                            <li key={index} className="my-2 md:my-0">
                                <Link
                                    to={path}
                                    className={`block py-1 md:py-3 pl-1 align-middle no-underline
                                        ${isActive ? 'text-indigo-400 font-bold' : 'text-gray-600'}
                                        hover:text-indigo-400`}
                                >
                                    <i className={`fas ${icon} fa-fw mr-3`}></i>
                                    <span className="w-full inline-block pb-1 md:pb-0 text-sm">{text}</span>
                                </Link>
                            </li>
                        );
                    })}
            </ul>
        </div>
    );
}
