// Sidebar.jsx
import { Link } from 'react-router-dom';
export default function Sidebar() {
    return (
        <div id="sidebar" className="h-screen w-16 menu bg-white text-white px-4 flex items-center nunito static fixed shadow">
            <ul className="list-reset">
                {[
                    { icon: "fa fa-plus", text: "HomePage", path: "/" },
                    { icon: "fa fa-filter", text: "AnalyticsPage", path: "/analytics" },
                    { icon: "fa-envelope", text: "Messages", path: "/messages" },
                ].map(({ icon, text, path }, index) => (
                    <li key={index} className="my-2 md:my-0">
                        <Link
                            to={path}
                            className="block py-1 md:py-3 pl-1 align-middle text-gray-600 no-underline hover:text-indigo-400"
                        >
                            <i className={`fas ${icon} fa-fw mr-3`}></i>
                            <span className="w-full inline-block pb-1 md:pb-0 text-sm">{text}</span>
                        </Link>
                    </li>
                ))}
            </ul>
        </div>
    );
}
