// src/components/AnalyticsContent.js
import React, { useEffect, useState, useRef } from 'react'; // Import useRef
import Chartist from 'chartist'; // Import Chartist
import 'chartist/dist/chartist.min.css'; // Import Chartist CSS (or in your main styles)

import api from '../utils/api';
// useFieldContext більше не потрібен тут

const AnalyticsContent = ({ selectedFieldId }) => {
    const [timeRange, setTimeRange] = useState('7d'); // Стан для діапазону часу
    const [chartData, setChartData] = useState(null); // Стан для даних графіка
    const [isLoading, setIsLoading] = useState(false); // Стан для індикатора завантаження
    const [error, setError] = useState(null); // Стан для помилки

    // ✅ Ref to the chart container element
    const chartContainerRef = useRef(null);
    // ✅ Ref to store the Chartist instance
    const chartInstanceRef = useRef(null);

    // Functions for backend select/deselect (as previously modified)
    const selectField = async () => { /* ... same as before ... */ };
    const deselectField = async () => { /* ... same as before ... */ };

    // Effect for fetching data and managing selection
    useEffect(() => {
        if (!selectedFieldId) {
            setChartData(null);
            setError(null);
            setIsLoading(false);
            // Cleanup from previous selection handled by the return function
            return;
        }

        // Call selectField when selectedFieldId changes
        selectField();

        // Fetch chart data
        setIsLoading(true);
        setError(null);
        const apiUrl = `/field-measurements/chart/${selectedFieldId}/${timeRange}`;

        api.get(apiUrl)
            .then(response => {
                // Assuming response.data is in Chartist compatible format: { labels: [...], series: [...] }
                // If your backend sends aggregatedData, you'll need to transform it here.
                // Based on endpoint name /chart, expecting time-series data.
                setChartData(response.data);
                setIsLoading(false);
            })
            .catch(err => {
                console.error('Error fetching chart data:', err);
                setError('Не вдалося завантажити дані графіка.');
                setChartData(null);
                setIsLoading(false);
            });

        // Cleanup function to deselect the field
        return () => {
            deselectField();
        };

    }, [selectedFieldId, timeRange]); // Dependencies

    // ✅ Effect for rendering the chart when chartData changes
    useEffect(() => {
        // Ensure we have data, the container element, and it's not loading or erroring
        if (chartData && chartContainerRef.current && !isLoading && !error) {
            // Destroy previous chart instance if it exists
            if (chartInstanceRef.current) {
                chartInstanceRef.current.detach();
            }

            // ✅ Define Chartist options (can be customized)
            const options = {
                low: 0, // Start the Y-axis at 0
                // showArea: true, // Uncomment if you want area under the line
                // showPoint: false, // Uncomment if you don't want points on the line
                fullWidth: true, // Use full width of the container
                chartPadding: { // Add some padding
                    right: 40
                },
                axisX: {
                    // Example for formatting dates if labels are Date objects or strings
                    // labelInterpolationFnc: function(value) {
                    //    return moment(value).format('MMM D'); // Needs moment.js or similar
                    // }
                    labelOffset: {
                        x: 0,
                        y: 5
                    }
                },
                axisY: {
                    labelInterpolationFnc: function(value) {
                        // Basic formatting, assumes value is a number
                        return value; // Or format based on sensor type if known
                    },
                    offset: 40
                }
            };

            // ✅ Create a new Chartist instance
            chartInstanceRef.current = new Chartist.Line(
                chartContainerRef.current,
                chartData, // Pass the fetched data
                options
            );

            // Optional: Re-apply animations if desired (adapt from your HTML)
            // Example animation for lines/areas
            chartInstanceRef.current.on('draw', function(data) {
                if (data.type === 'line' || data.type === 'area') {
                    data.element.animate({
                        d: {
                            begin: 1000 * data.index,
                            dur: 1000,
                            from: data.path.clone().scale(1, 0).translate(0, data.chartRect.height()).stringify(),
                            to: data.path.clone().stringify(),
                            easing: Chartist.Svg.Easing.easeOutQuint
                        }
                    });
                }
            });


        } else if (!chartData && chartInstanceRef.current) {
            // If chartData becomes null (e.g., field deselected), destroy the chart
            chartInstanceRef.current.detach();
            chartInstanceRef.current = null;
        }

        // ✅ Cleanup function for the chart instance
        return () => {
            if (chartInstanceRef.current) {
                chartInstanceRef.current.detach();
                chartInstanceRef.current = null; // Clear the ref
            }
        };

    }, [chartData, isLoading, error]); // Dependencies: re-run when chartData, isLoading, or error change


    // ✅ Функція для зміни діапазону часу (залишається без змін)
    const handleTimeRangeChange = (newTimeRange) => {
        setTimeRange(newTimeRange);
    };

    return (
        <div id="main-content" className="w-full flex-1">
            <div className="flex flex-1 flex-wrap">
                {/* Adjusted width slightly */}
                <div className="w-full xl:w-2/3 p-6 xl:max-w-6xl">
                    {/* "Container" for the graphs"*/}
                    <div className="max-w-full lg:max-w-3xl xl:max-w-5xl">

                        {/*Graph Card*/}
                        <div className="border-b p-3">
                            {/* Відображаємо ID поля, якщо воно вибрано */}
                            <h5 className="font-bold text-black">Графік вимірювань {selectedFieldId ? `для поля ID: ${selectedFieldId}` : ''}</h5>
                            {/* Кнопки вибору масштабування */}
                            <div className="flex space-x-4 mt-2"> {/* Added mt-2 for spacing */}
                                <button
                                    onClick={() => handleTimeRangeChange('7d')}
                                    className={`px-3 py-1 text-sm rounded ${timeRange === '7d' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    7 днів
                                </button>
                                <button
                                    onClick={() => handleTimeRangeChange('6m')}
                                    className={`px-3 py-1 text-sm rounded ${timeRange === '6m' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    6 місяців
                                </button>
                                <button
                                    onClick={() => handleTimeRangeChange('1y')}
                                    className={`px-3 py-1 text-sm rounded ${timeRange === '1y' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    1 рік
                                </button>
                                {/* Add a button for All Time if needed */}
                                {/* <button
                                    onClick={() => handleTimeRangeChange('all')}
                                    className={`px-3 py-1 text-sm rounded ${timeRange === 'all' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    Весь час
                                </button> */}
                            </div>
                        </div>
                        <div className="p-5">
                            {/* ✅ Assign the ref to the chart container div */}
                            <div className="ct-chart ct-golden-section" id="chart1" ref={chartContainerRef}>
                                {/* ✅ Show messages conditionally, chart will be drawn here by Chartist */}
                                {!selectedFieldId && (
                                    <p className="text-center text-gray-500">Будь ласка, оберіть поле зі списку ліворуч, щоб побачити графік.</p>
                                )}
                                {selectedFieldId && isLoading && (
                                    <p className="text-center text-gray-500">Завантаження даних графіка...</p>
                                )}
                                {selectedFieldId && error && (
                                    <p className="text-center text-red-500">{error}</p>
                                )}
                                {/* Show 'no data' message only if field selected, not loading, no error, and data is empty/null */}
                                {selectedFieldId && !isLoading && !error && (!chartData || (Array.isArray(chartData.series) && chartData.series.every(s => s.length === 0)) || (Array.isArray(chartData.labels) && chartData.labels.length === 0)) && (
                                    <p className="text-center text-gray-500">Дані для обраного поля за цей період не знайдені.</p>
                                )}
                                {/* The Chartist graph will be rendered *into* this div */}
                            </div>
                        </div>
                        {/* /Graph Card*/}

                        {/* Table Card - Keeping as is for now */}
                        {/* TODO: Maybe make this dynamic with selected field data? */}
                        <div className="p-3">
                            <div className="border-b p-3">
                                <h5 className="font-bold text-black">Table</h5>
                            </div>
                            <div className="p-5">
                                <table className="w-full p-5 text-gray-700">
                                    <thead>
                                    <tr>
                                        <th className="text-left text-blue-900">Name</th>
                                        <th className="text-left text-blue-900">Side</th>
                                        <th className="text-left text-blue-900">Role</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    <tr>
                                        <td>Obi Wan Kenobi</td>
                                        <td>Light</td>
                                        <td>Jedi</td>
                                    </tr>
                                    <tr>
                                        <td>Greedo</td>
                                        <td>South</td>
                                        <td>Scumbag</td>
                                    </tr>
                                    <tr>
                                        <td>Darth Vader</td>
                                        <td>Dark</td>
                                        <td>Sith</td>
                                    </tr>
                                    </tbody>
                                </table>
                                <p className="py-2"><a href="#">See More issues...</a></p>
                            </div>
                        </div>
                        {/* /table Card*/}

                    </div>

                </div>

                {/* Second Column - Keeping as is for now */}
                {/* TODO: These extra charts (#chart2, #chart3, #chart4) are hardcoded examples.
                         If you need more dynamic charts for different sensor types or data,
                         you'll need to fetch more data and create separate Chartist instances
                         or a multi-chart component. For now, they are just empty divs. */}
                <div className="w-full xl:w-1/3 p-6 xl:max-w-4xl border-l-1 border-gray-300">

                    {/* "Container" for the graphs"*/}
                    <div className="max-w-sm lg:max-w-3xl xl:max-w-5xl">

                        {/*Graph Card*/}
                        <div className="border-b p-3">
                            <h5 className="font-bold text-black">Graph 2</h5> {/* Labelled differently */}
                        </div>
                        <div className="p-5">
                            {/* ✅ These divs will also need refs if you want to draw charts into them */}
                            <div className="ct-chart ct-golden-section" id="chart2"></div>
                        </div>
                        {/*/Graph Card*/}

                        {/*Graph Card*/}
                        <div className="border-b p-3">
                            <h5 className="font-bold text-black">Graph 3</h5> {/* Labelled differently */}
                        </div>
                        <div className="p-5">
                            {/* ✅ These divs will also need refs if you want to draw charts into them */}
                            <div className="ct-chart ct-golden-section" id="chart3"></div>
                        </div>
                        {/*/Graph Card*/}

                        {/*Graph Card*/}
                        <div className="border-b p-3">
                            <h5 className="font-bold text-black">Graph 4</h5> {/* Labelled differently */}
                        </div>
                        <div className="p-5">
                            {/* ✅ These divs will also need refs if you want to draw charts into them */}
                            <div className="ct-chart ct-golden-section" id="chart4"></div>
                        </div>
                        {/*/Graph Card*/}

                        {/*Template Card - Keeping as is */}
                        <div className="p-3">
                            <div className="border-b p-3">
                                <h5 className="font-bold text-black">Template</h5>
                            </div>
                            <div className="p-5">

                            </div>
                        </div>
                        {/*/Template Card*/}

                    </div>

                </div>

            </div>

        </div>

    );
}

export default AnalyticsContent;
