import React from 'react';
import { Button } from '@/components/ui/button';
import { useNavigate } from 'react-router-dom';

const categories = [
    {
        title: 'Користувачі',
        baseRoute: '/admin/users',
        actions: [
            { label: 'Отримати всіх', route: '' },
            { label: 'Отримати за ID', route: '/:id' },
            { label: 'Оновити', route: '/:id' },
            { label: 'Видалити', route: '/:id' },
        ],
    },
    {
        title: 'Пристрої',
        baseRoute: '/admin/devices',
        actions: [
            { label: 'Отримати всі', route: '' },
            { label: 'Отримати за ID', route: '/:id' },
            { label: 'Додати', route: '/create' },
            { label: 'Оновити', route: '/:id' },
            { label: 'Видалити', route: '/:id' },
        ],
    },
    {
        title: 'Датчики',
        baseRoute: '/admin/sensors',
        actions: [
            { label: 'Отримати всі', route: '' },
            { label: 'Отримати за ID', route: '/:id' },
            { label: 'Додати', route: '/create' },
            { label: 'Оновити', route: '/:id' },
            { label: 'Видалити', route: '/:id' },
        ],
    },
    {
        title: 'Датчики користувачів',
        baseRoute: '/admin/user-devices',
        actions: [
            { label: 'Отримати всі (по собі)', route: '' },
            { label: 'Отримати по користувачу', route: '/:userId' },
            { label: 'Привʼязати', route: '/create' },
            { label: 'Оновити звʼязок', route: '/:id' },
            { label: 'Відвʼязати', route: '/:id' },
        ],
    },
];

export default function AdminDashboard() {
    const navigate = useNavigate();

    return (
        <div className="p-6 space-y-8">
            <h1 className="text-3xl font-bold">Адмін Панель</h1>

            {categories.map((category, i) => (
                <div key={i} className="border rounded-2xl p-4 shadow-md bg-white">
                    <h2 className="text-xl font-semibold mb-4">{category.title}</h2>
                    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
                        {category.actions.map((action, idx) => (
                            <Button
                                key={idx}
                                variant="outline"
                                onClick={() => navigate(`${category.baseRoute}${action.route}`)}
                            >
                                {action.label}
                            </Button>
                        ))}
                    </div>
                </div>
            ))}
        </div>
    );
}
