// pages/LoginPage.js
import React, { useState, useContext, useEffect } from 'react'; // Додали useContext, useEffect
import AuthForm from '../components/AuthForm';
import api from '../utils/api';
import { useNavigate } from 'react-router-dom';
import { auth, googleProvider } from '../firebase';
import { signInWithPopup } from 'firebase/auth';
import { UserContext } from '../contexts/UserContext'; // Імпортуємо UserContext

export default function LoginPage() {
    const navigate = useNavigate();

    // Отримуємо user, loading та функцію fetchUser з контексту
    const { user, loading: userLoading, fetchUser } = useContext(UserContext);
    const [loginInProgress, setLoginInProgress] = useState(false); // Стан для індикатора завантаження на кнопках

    // Ефект для перенаправлення, якщо користувач вже авторизований
    useEffect(() => {
        // Перевіряємо, чи завантаження контексту завершено І користувач не є null
        if (!userLoading && user) {
            console.log('LoginPage: Користувач вже авторизований, перенаправлення на /');
            // Перенаправляємо на головну сторінку
            navigate('/', { replace: true }); // replace=true заміняє поточний запис в історії
        }
    }, [user, userLoading, navigate]); // Ефект залежить від user, userLoading та navigate

    const handleLogin = async (email, password) => {
        setLoginInProgress(true); // Починаємо завантаження
        try {
            const res = await api.post('/auth/login/password', { email, password }, {
                withCredentials: true
            });
            console.log('Password login success:', res.data);

            // !!! ВАЖЛИВО: !!!
            // Якщо ваш бекенд використовує HTTP-only куки для сесії,
            // то браузер автоматично надсилатиме їх з наступними запитами до вашого домену.
            // У цьому випадку вам не потрібно робити нічого додатково після отримання успішної відповіді,
            // окрім виклику fetchUser().

            // Якщо ваш бекенд повертає токен у тілі відповіді (не куку),
            // вам потрібно ЗБЕРЕГТИ цей токен (наприклад, у localStorage)
            // І НАЛАШТУВАТИ ваш 'api' інстанс (axios)
            // Щоб він додавав цей токен у заголовок Authorization: Bearer <токен>
            // для всіх наступних запитів. Це налаштування зазвичай робиться один раз
            // при ініціалізації api інстанса або після отримання токена.

            // --- КЛЮЧОВИЙ КРОК ---
            // Викликаємо функцію fetchUser з контексту для оновлення стану користувача
            await fetchUser();

            // Прибираємо navigate('/home'); - тепер цим займається useEffect вище
            // або ProtectedRoute, коли контекст оновиться.

        } catch (err) {
            console.error('Password login error:', err.response?.data || err.message);
            alert('Помилка входу: ' + (err.response?.data?.message || err.message));
        } finally {
            setLoginInProgress(false); // Завершуємо завантаження
        }
    };

    const handleGoogleLogin = async () => {
        setLoginInProgress(true); // Починаємо завантаження
        try {
            const result = await signInWithPopup(auth, googleProvider);
            const token = await result.user.getIdToken(); // Отримуємо токен Firebase

            // Надсилаємо токен Firebase на ваш бекенд для обміну на вашу внутрішню сесію/токен
            const res = await api.post('/auth/login/google', { token }, { withCredentials: true });
            console.log('Google login success:', res.data);

            // !!! ВАЖЛИВО: !!!
            // Аналогічно до звичайного логіну, переконайтеся, що ваш бекенд
            // встановлює куки або повертає токен, і ваш api інстанс коректно його обробляє.

            // --- КЛЮЧОВИЙ КРОК ---
            // Викликаємо функцію fetchUser з контексту для оновлення стану користувача
            await fetchUser();

            // Прибираємо navigate('/home');

        } catch (error) {
            console.error('Google login error:', error);
            alert('Помилка авторизації через Google: ' + error.message);
        } finally {
            setLoginInProgress(false); // Завершуємо завантаження
        }
    };

    // Якщо контекст користувача ще завантажується, можна показати індикатор або нічого
    // Це корисно, щоб уникнути блимання форми логіну перед перенаправленням,
    // якщо користувач вже був авторизований.
    if (userLoading) {
        return <div>Завантаження статусу авторизації...</div>; // Або просто поверніть null
    }

    // Якщо userLoading завершено І user є null (користувач не авторизований),
    // тоді рендеримо форму логіну. useEffect вище обробить випадок, коли user не null.
    return (
        <AuthForm
            type="login"
            onSubmit={handleLogin}
            onGoogleLogin={handleGoogleLogin}
            isSubmitting={loginInProgress} // Передаємо стан завантаження у форму
        />
    );
}
