import React, { useEffect, useState, useRef, useCallback } from 'react'; // Додано useCallback
import { useParams } from "react-router-dom";
import { Line as LineChart, Svg } from 'chartist';
import 'chartist/dist/chartist.css';
import api from '../utils/api';

const AnalyticsField = () => {
    const { id } = useParams(); // ID поточного вибраного поля з URL

    const [timeRange, setTimeRange] = useState('7d');
    const [chartData, setChartData] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const chartContainerRef = useRef(null);
    const chartInstanceRef = useRef(null); // Для збереження інстансу графіка

    // --- API Функції ---
    // Використовуємо useCallback, щоб функції не створювались заново при кожному рендері,
    // якщо їхні залежності не змінились (хоча тут залежностей немає, це хороша практика)
    // API Функція для вибору поля
    const selectField = useCallback(async (fieldId) => {
        if (!fieldId) {
            console.warn('[AnalyticsField:selectField] Спроба вибору поля без ID.');
            return;
        }
        try {
            console.log(`[AnalyticsField:selectField] Відправка запиту на select поля ID: ${fieldId}.`);
            await api.get(`/fields/select/${fieldId}`); // Замінили на .get
            console.log(`[AnalyticsField:selectField] Поле ID ${fieldId} успішно вибрано.`);
        } catch (err) {
            console.error(`[AnalyticsField:selectField] Помилка при виборі поля ID ${fieldId}:`, err);
            throw new Error(`Не вдалося вибрати поле ID ${fieldId}.`);
        }
    }, []);

// API Функція для деселекту поля
    const deselectField = useCallback(async (fieldId) => {
        if (!fieldId) {
            console.warn('[AnalyticsField:deselectField] Спроба деселекту поля без ID.');
            return;
        }
        try {
            console.log(`[AnalyticsField:deselectField] Відправка запиту на deselect поля ID: ${fieldId}.`);
            await api.get(`/fields/deselect/${fieldId}`); // Замінили на .get
            console.log(`[AnalyticsField:deselectField:SUCCESS] Поле ID ${fieldId} успішно деселектнуто.`);
        } catch (err) {
            console.error(`[AnalyticsField:deselectField:ERROR] Не вдалося деселектнути поле ID ${fieldId}:`, err);
        }
    }, []);



    useEffect(() => {
        if (!id) {
            console.log('[AnalyticsField:useEffect:data] Немає ID в URL, очищення стану.');
            setChartData(null);
            setError(null);
            setIsLoading(false);
            return;
        }

        const fetchDataForSelectedField = async () => {
            setIsLoading(true);
            setError(null);
            setChartData(null);

            try {
                await selectField(id);

                const apiUrl = `/field-measurements/chart/${timeRange}`;
                console.log(`[AnalyticsField:useEffect:data] Запит даних графіка з: ${apiUrl} (після вибору поля ${id})`);
                const response = await api.get(apiUrl);

                const rawData = response.data?.aggregatedData;

                if (!Array.isArray(rawData)) {
                    throw new Error('Некоректний формат відповіді від сервера або дані відсутні.');
                }

                // Трансформація даних (логіка не змінюється)
                const uniqueTimestampsSet = new Set();
                rawData.forEach(sensor => {
                    if (sensor && Array.isArray(sensor.data)) {
                        sensor.data.forEach(entry => {
                            if (entry && entry.timestamp) {
                                uniqueTimestampsSet.add(entry.timestamp);
                            }
                        });
                    }
                });

                const labels = Array.from(uniqueTimestampsSet).sort((a, b) => new Date(a) - new Date(b));
                const series = rawData.map(sensor => {
                    const timestampToValueMap = new Map();
                    if (sensor && Array.isArray(sensor.data)) {
                        sensor.data.forEach(entry => {
                            if (entry && entry.timestamp && entry.value !== undefined && entry.value !== null) {
                                timestampToValueMap.set(entry.timestamp, entry.value);
                            }
                        });
                    }
                    const seriesData = labels.map(timestamp => timestampToValueMap.get(timestamp) ?? null);
                    return {
                        name: sensor.sensorType || `Sensor ${sensor.sensorId || 'Unknown'}`,
                        data: seriesData
                    };
                });

                const chartistSeriesData = series.map(s => s.data);

                const hasDataPoints = chartistSeriesData.some(seriesData =>
                    Array.isArray(seriesData) && seriesData.some(point => point !== null)
                );

                if (labels.length === 0 || !hasDataPoints) {
                    setChartData(null);
                } else {
                    setChartData({ labels, series: chartistSeriesData });
                }
                setError(null);
            } catch (err) {
                console.error('[AnalyticsField:useEffect:data] Помилка під час вибору поля або завантаження даних:', err);
                setError(err.message || 'Не вдалося завантажити дані графіка.');
                setChartData(null);
            } finally {
                setIsLoading(false);
            }
        };

        fetchDataForSelectedField();

        return () => {
            const fieldIdToDeselect = id;
            return () => {
                console.log(`[AnalyticsField:useEffect:data] Запуск очищення для ID: ${fieldIdToDeselect}`);
                if (fieldIdToDeselect) {
                    deselectField(fieldIdToDeselect);
                }
            };
        };
    }, [id, timeRange, selectField, deselectField]);


    useEffect(() => {
        const fetchDataForSelectedField = async () => {
            setIsLoading(true);
            setError(null);
            setChartData(null); // Reset previous data

            try {
                await selectField(id);

                const apiUrl = `/field-measurements/chart/${timeRange}`;
                const response = await api.get(apiUrl);
                const rawData = response.data?.aggregatedData;

                if (!Array.isArray(rawData)) {
                    throw new Error('Invalid data format.');
                }

                const uniqueTimestampsSet = new Set();
                rawData.forEach(sensor => {
                    if (sensor && Array.isArray(sensor.data)) {
                        sensor.data.forEach(entry => {
                            if (entry && entry.timestamp) {
                                uniqueTimestampsSet.add(entry.timestamp);
                            }
                        });
                    }
                });

                const labels = Array.from(uniqueTimestampsSet).sort((a, b) => new Date(a) - new Date(b));

                const series = rawData.map(sensor => {
                    const timestampToValueMap = new Map();
                    if (sensor && Array.isArray(sensor.data)) {
                        sensor.data.forEach(entry => {
                            if (entry && entry.timestamp && entry.value !== undefined && entry.value !== null) {
                                timestampToValueMap.set(entry.timestamp, entry.value);
                            }
                        });
                    }
                    const seriesData = labels.map(timestamp => timestampToValueMap.get(timestamp) ?? null);
                    return { name: sensor.sensorType || `Sensor ${sensor.sensorId || 'Unknown'}`, data: seriesData };
                });

                const chartistSeriesData = series.map(s => s.data);
                const hasDataPoints = chartistSeriesData.some(seriesData =>
                    Array.isArray(seriesData) && seriesData.some(point => point !== null)
                );

                if (labels.length === 0 || !hasDataPoints) {
                    setChartData(null);
                } else {
                    setChartData({ labels, series: chartistSeriesData });
                }
                setError(null);
            } catch (err) {
                setError(err.message || 'Failed to load chart data.');
                setChartData(null);
            } finally {
                setIsLoading(false);
            }
        };

        fetchDataForSelectedField();

        const fieldIdToDeselect = id;
        return () => {
            if (fieldIdToDeselect) {
                deselectField(fieldIdToDeselect);
            }
        };
    }, [id, timeRange, selectField, deselectField]);

    // --- Обробник Зміни Діапазону Часу ---
    const handleTimeRangeChange = (range) => {
        // Не оновлюємо, якщо вибрано той самий діапазон або йде завантаження
        if (range !== timeRange && !isLoading) {
            setTimeRange(range);
        }
    };

    console.log(`[AnalyticsField] Рендер компонента. ID: ${id}, isLoading: ${isLoading}, error: ${error}, chartData: ${chartData ? 'є' : 'немає/порожньо'}`);

    // --- JSX Рендеринг ---
    return (
        // Використовуємо `h-full` або `min-h-screen` для батьківських контейнерів, якщо потрібно розтягнути контент
        <div id="main-content" className="w-full flex-1 bg-gray-100 p-6"> {/* Додано фон і падінг */}
            <div className="max-w-7xl mx-auto"> {/* Обмежуємо максимальну ширину */}
                <div className="bg-white shadow rounded-lg p-4 md:p-6"> {/* Картка для графіка */}
                    {/* Заголовок та кнопки вибору діапазону */}
                    <div className="flex justify-between items-center border-b border-gray-200 pb-3 mb-4">
                        <h5 className="text-xl font-bold text-gray-900">
                            Аналітика вимірювань {id ? `для поля ID: ${id}` : '(Поле не вибрано)'}
                        </h5>
                        <div className="flex space-x-2">
                            {['7d', '6m', '1y'].map(range => (
                                <button
                                    key={range}
                                    onClick={() => handleTimeRangeChange(range)}
                                    disabled={isLoading} // Блокуємо кнопки під час завантаження
                                    className={`px-3 py-1 text-sm font-medium rounded transition-colors duration-150 ease-in-out ${
                                        timeRange === range
                                            ? 'bg-blue-600 text-white hover:bg-blue-700'
                                            : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                                    } ${isLoading ? 'opacity-50 cursor-not-allowed' : ''}`} // Стилі для блокування
                                >
                                    {range === '7d' && '7 днів'}
                                    {range === '6m' && '6 міс'}
                                    {range === '1y' && '1 рік'}
                                </button>
                            ))}
                        </div>
                    </div>

                    {/* Контейнер для графіка та повідомлень */}
                    <div className="relative min-h-[350px]"> {/* Мінімальна висота для графіка */}
                        {/* Повідомлення стану */}
                        {!id && (
                            <div className="absolute inset-0 flex items-center justify-center">
                                <p className="text-center text-gray-500 text-lg">
                                    Будь ласка, оберіть поле зі списку.
                                </p>
                            </div>
                        )}
                        {id && isLoading && (
                            <div className="absolute inset-0 flex items-center justify-center bg-white bg-opacity-75 z-10">
                                <p className="text-center text-gray-600 text-lg animate-pulse">Завантаження даних...</p>
                                {/* Можна додати спінер тут */}
                            </div>
                        )}
                        {id && error && !isLoading && (
                            <div className="absolute inset-0 flex items-center justify-center">
                                <p className="text-center text-red-600 text-lg px-4 py-2 bg-red-100 rounded border border-red-300">{error}</p>
                            </div>
                        )}
                        {/* Повідомлення "Немає даних" */}
                        {id && !isLoading && !error && !chartData && ( // Показуємо, якщо не завантаження, немає помилки і chartData = null
                            <div className="absolute inset-0 flex items-center justify-center">
                                <p className="text-center text-gray-500 text-lg">
                                    Дані для обраного поля за цей період відсутні.
                                </p>
                            </div>
                        )}

                        {/* Власник контейнер для графіка */}
                        {/* Ми додаємо/видаляємо графік динамічно, тому повідомлення показуються поверх */}
                        <div
                            className={`ct-chart ct-golden-section ${!id || isLoading || error || !chartData ? 'opacity-0' : 'opacity-100'} transition-opacity duration-300`} // Ховаємо контейнер, якщо немає що показувати
                            ref={chartContainerRef}
                            style={{minHeight: '350px'}} // Забезпечуємо мінімальну висоту
                        >
                            {/* Chartist буде малювати сюди */}
                        </div>
                    </div>
                </div>

                {/* --- Видалено секцію з таблицею та правою колонкою --- */}
                {/* Якщо вони потрібні, їх треба реалізувати з відповідною логікою */}
                {/*
                 <div className="mt-6 bg-white shadow rounded-lg p-4 md:p-6">
                     <h5 className="text-xl font-bold text-gray-900 border-b pb-3 mb-4">Додаткова інформація</h5>
                     { // Тут може бути таблиця з даними або інші елементи
                     }
                 </div>
                 */}
            </div>
        </div>
    );
};

export default AnalyticsField;
