// src/pages/HomePage.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import DashboardContent from '../components/DashboardContent';
import MainContent from '../components/MainContent.jsx';

const HomePage = () => {
    const navigate = useNavigate(); // Отримуємо функцію navigate

    // Функція-обробник для навігації
    const handleFieldSelectForHome = (fieldId) => {
        console.log('HomePage: handleFieldSelectForHome called with ID:', fieldId); // Лог: функція викликана
        console.log(`HomePage: Attempting navigation to /field/${fieldId}`); // Лог: спроба навігації
        navigate(`/field/${fieldId}`); // Виконуємо навігацію
    };

    return (
        <div className="flex flex-wrap">
            {/* Передаємо обробник навігації та selectedFieldId={null} */}
            <DashboardContent
                onFieldSelect={handleFieldSelectForHome}
                selectedFieldId={null} // На домашній сторінці не виділяємо
            />
            {/* MainContent залишається як був */}
            <MainContent />
        </div>
    );
}

export default HomePage;
