import React from 'react';
import DashboardContent from '../components/DashboardContent';
import MainContent from '../components/MainContent';
import CreateField from '../components/CreateField'; // Імпортуємо CreateField
import { useFieldContext } from '../FieldContext'; // Хук для доступу до контексту

const HomePage = () => {
    const { isCreatingField, setIsCreatingField } = useFieldContext(); // Отримуємо доступ до стану

    return (
        <div className="flex flex-col">
            <div className="flex">
                <DashboardContent />
                {isCreatingField ? (
                    <CreateField /> // Показуємо CreateField, якщо isCreatingField = true
                ) : (
                    <MainContent /> // Інакше показуємо MainContent
                )}
            </div>
        </div>
    );
}

export default HomePage;
