import React, { useCallback, useState } from "react";
import { GoogleMap, LoadScript, Polygon } from "@react-google-maps/api";

const containerStyle = {
    width: "100%",
    height: "500px",
};

const center = {
    lat: 48.3794, // Україна, можеш змінити на координати поля
    lng: 31.1656,
};

const MapPolygonPicker = ({ onPolygonComplete }) => {
    const [paths, setPaths] = useState([]);
    const [manualCoords, setManualCoords] = useState("");
    const mapRef = useRef(null);

    const handleClick = useCallback(
        (event) => {
            const newPoint = {
                lat: event.latLng.lat(),
                lng: event.latLng.lng(),
            };

            // Додаємо точку до масиву paths
            setPaths((prev) => {
                const newPaths = [...prev, newPoint];

                // Оновлюємо текстове поле координат
                const coordsString = newPaths
                    .map((point) => `[${point.lat.toFixed(4)}, ${point.lng.toFixed(4)}]`)
                    .join(",\n");

                setManualCoords(coordsString);
                return newPaths;
            });
        },
        [paths]
    );

    const handleComplete = () => {
        if (onPolygonComplete) onPolygonComplete(paths);
    };

    return (
        <div>
            <LoadScript googleMapsApiKey="AIzaSyC6-KT4EaMNrVuln5DQ5Xn8e8n4kZwsKMU">
                <GoogleMap
                    mapContainerStyle={containerStyle}
                    center={center}
                    zoom={6}
                    onClick={handleClick}
                    onLoad={(map) => (mapRef.current = map)}
                >
                    {paths.length > 0 && (
                        <Polygon
                            path={paths}
                            options={{
                                fillColor: "#00FF00",
                                fillOpacity: 0.3,
                                strokeColor: "#00AA00",
                                strokeOpacity: 1,
                                strokeWeight: 2,
                                editable: true,
                                draggable: false,
                            }}
                        />
                    )}
                </GoogleMap>
            </LoadScript>

            {/* Поле для введення координат */}
            <div className="my-6">
                <h6 className="font-semibold mb-2">Або введіть координати тут:</h6>
                <textarea
                    rows="6"
                    value={manualCoords}
                    onChange={(e) => setManualCoords(e.target.value)}
                    className="w-full border border-gray-300 p-2 rounded-lg font-mono text-sm"
                    placeholder={`[40.7484, -73.9857],\n[40.7488, -73.9855],\n[40.7486, -73.9842],\n[40.7482, -73.9844],\n[40.7484, -73.9857]`}
                />
                <p className="text-xs text-gray-500 mt-2">
                    Введіть координати у форматі: <code>[широта, довгота]</code>, кожна пара з нового рядка. Має утворювати замкнений полігон.
                </p>
            </div>

            {/* Кнопка підтвердження */}
            <button className="mt-2 bg-green-500 text-white px-4 py-2 rounded" onClick={handleComplete}>
                Підтвердити полігон
            </button>
        </div>
    );
};

export default MapPolygonPicker;
