import React from 'react';
import AuthForm from '../components/AuthForm';
import api from '../utils/api';
import { useNavigate } from 'react-router-dom';
import { signInWithPopup } from "firebase/auth";
import { auth, googleProvider } from "../firebase";

export default function RegisterPage() {
    const navigate = useNavigate();

    const handleRegister = async (email, password) => {
        try {
            const res = await api.post('/auth/register/password', { email, password });
            console.log('Register success:', res.data);
            const { user } = res.data;

            localStorage.setItem('userName', user.name);
            localStorage.setItem('isAdmin', user.is_admin); // зберігаємо роль
            navigate('/login'); // або одразу /home — як хочеш
        } catch (err) {
            alert('Помилка реєстрації: ' + (err.response?.data?.message || err.message));
        }
    };

    const handleGoogleRegister = async () => {
        try {
            const result = await signInWithPopup(auth, googleProvider);
            const token = await result.user.getIdToken();

            const res = await api.post('/auth/login/google', { token }, { withCredentials: true });
            console.log('Google login success:', res.data);
            const { user } = res.data;

            localStorage.setItem('userName', user.name);
            localStorage.setItem('isAdmin', user.is_admin); // зберігаємо роль
            navigate('/home');
        } catch (error) {
            console.error('Google login error:', error);
            alert('Помилка авторизації через Google');
        }
    };


    return (
        <AuthForm
            type="register"
            onSubmit={handleRegister}
            onGoogleLogin={handleGoogleRegister}
        />
    );
}
