import React, { useEffect, useState, useRef } from 'react';
import Chartist from 'chartist'; // Імпортуємо Chartist як за замовчуванням
import 'chartist/dist/chartist.css';

import api from '../utils/api';

const AnalyticsField = ({ selectedFieldId }) => {

    const [timeRange, setTimeRange] = useState('7d');
    const [chartData, setChartData] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const chartContainerRef = useRef(null);
    const chartInstanceRef = useRef(null);

    const selectField = async () => {
        try {
            await api.post('/fields/select/', { fieldId: id });
        } catch (err) {
            console.error('Не вдалося вибрати поле:', err);
        }
    };

    const deselectField = async () => {
        try {
            await api.post('/fields/deselect/', { fieldId: id });
        } catch (err) {
            console.error('Не вдалося деселектнути поле:', err);
        }
    };

    useEffect(() => {
        if (!selectedFieldId) {
            setChartData(null);
            setError(null);
            setIsLoading(false);
            return;
        }

        selectField();
        setIsLoading(true);
        setError(null);

        const apiUrl = `/field-measurements/chart/${timeRange}`;
        api.get(apiUrl)
            .then(response => {
                const rawData = response.data.aggregatedData;

                if (!Array.isArray(rawData)) {
                    setChartData(null);
                    setError('Некоректна відповідь від сервера.');
                    setIsLoading(false);
                    return;
                }

                // Збір унікальних дат
                const uniqueDatesSet = new Set();
                rawData.forEach(sensor => {
                    sensor.data.forEach(entry => {
                        uniqueDatesSet.add(entry.timestamp);
                    });
                });
                const labels = Array.from(uniqueDatesSet).sort(); // Сортуємо дати

                // Побудова серій даних
                const series = rawData.map(sensor => {
                    const dateToValueMap = new Map(sensor.data.map(entry => [entry.timestamp, entry.value]));

                    return labels.map(date => dateToValueMap.get(date) ?? null); // null — пропуск, якщо немає даних
                });

                // Назви сенсорів (опціонально для легенди або консольної відладки)
                const sensorTypes = rawData.map(sensor => sensor.sensorType);

                setChartData({ labels, series });
                setIsLoading(false);
            })
            .catch(err => {
                setError('Не вдалося завантажити дані графіка.');
                setChartData(null);
                setIsLoading(false);
                console.error(err);
            });


        return () => {
            deselectField();
        };
    }, [selectedFieldId, timeRange]);

    useEffect(() => {
        if (
            chartContainerRef.current &&
            chartData &&
            Array.isArray(chartData.series) &&
            chartData.series.length > 0 &&
            !isLoading &&
            !error
        ) {
            if (chartInstanceRef.current) {
                chartInstanceRef.current.detach();
            }

            const options = {
                low: 0,
                fullWidth: true,
                chartPadding: { right: 40 },
                axisX: {
                    labelOffset: { x: 0, y: 5 }
                },
                axisY: {
                    offset: 40,
                    labelInterpolationFnc: value => value
                }
            };

            chartInstanceRef.current = new Chartist.Line(chartContainerRef.current, chartData, options);

            chartInstanceRef.current.on('draw', data => {
                if (data.type === 'line' || data.type === 'area') {
                    data.element.animate({
                        d: {
                            begin: 500 * data.index,
                            dur: 1000,
                            from: data.path.clone().scale(1, 0).translate(0, data.chartRect.height()).stringify(),
                            to: data.path.clone().stringify(),
                            easing: Chartist.Svg.Easing.easeOutQuint
                        }
                    });
                }
            });
        } else if (chartInstanceRef.current) {
            chartInstanceRef.current.detach();
            chartInstanceRef.current = null;
        }

        return () => {
            if (chartInstanceRef.current) {
                chartInstanceRef.current.detach();
                chartInstanceRef.current = null;
            }
        };
    }, [chartData, isLoading, error]);

    const handleTimeRangeChange = (range) => {
        setTimeRange(range);
    };

    return (
        <div id="main-content" className="w-full flex-1">
            <div className="flex flex-wrap">
                <div className="w-full xl:w-2/3 p-6 xl:max-w-6xl">
                    <div className="max-w-full lg:max-w-3xl xl:max-w-5xl">
                        <div className="border-b p-3">
                            <h5 className="font-bold text-black">
                                Графік вимірювань {selectedFieldId ? `для поля ID: ${selectedFieldId}` : ''}
                            </h5>
                            <div className="flex space-x-4 mt-2">
                                {['7d', '6m', '1y'].map(range => (
                                    <button
                                        key={range}
                                        onClick={() => handleTimeRangeChange(range)}
                                        className={`px-3 py-1 text-sm rounded ${timeRange === range
                                            ? 'bg-indigo-500 text-white'
                                            : 'bg-gray-300 text-gray-800'
                                        }`}
                                    >
                                        {range === '7d' && '7 днів'}
                                        {range === '6m' && '6 місяців'}
                                        {range === '1y' && '1 рік'}
                                    </button>
                                ))}
                            </div>
                        </div>

                        <div className="p-5">
                            <div className="ct-chart ct-golden-section" id="chart1" ref={chartContainerRef}>
                                {!selectedFieldId && (
                                    <p className="text-center text-gray-500">
                                        Будь ласка, оберіть поле зі списку ліворуч.
                                    </p>
                                )}
                                {selectedFieldId && isLoading && (
                                    <p className="text-center text-gray-500">Завантаження даних...</p>
                                )}
                                {error && (
                                    <p className="text-center text-red-500">{error}</p>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AnalyticsField;
