// src/pages/HomePage.js
import React from 'react';
import { useNavigate } from 'react-router-dom'; // Імпортуємо хук для навігації
import DashboardContent from '../components/DashboardContent';
import MainContent from '../components/MainContent.jsx'; // Залишаємо MainContent

const HomePage = () => {
    const navigate = useNavigate(); // Отримуємо функцію для програмної навігації

    // Функція-обробник, яка буде викликана при кліку на полі в DashboardContent на цій сторінці
    const handleFieldSelectForHome = (fieldId) => {
        console.log(`Field ${fieldId} selected on Home Page. Navigating to /field/${fieldId}`);
        // Використовуємо функцію navigate для переходу на сторінку деталей поля
        navigate(`/field/${fieldId}`);
    };

    return (
        <div className="flex flex-wrap">
            {/*
              Рендеримо DashboardContent і передаємо:
              - onFieldSelect: функцію, яка виконує навігацію при кліку
              - selectedFieldId: null, оскільки на цій сторінці ми не виділяємо поле візуально після кліку,
                                 бо одразу переходимо на іншу сторінку.
            */}
            <DashboardContent
                onFieldSelect={handleFieldSelectForHome}
                selectedFieldId={null} // На домашній сторінці не виділяємо обране поле
            />
            {/*
              Рендеримо MainContent.jsx. За цим запитом його вміст не змінюється
              при кліку на полі, він залишається статичним або містить інший загальний контент домашньої сторінки.
            */}
            <MainContent />
        </div>
    );
}

export default HomePage;
