// contexts/UserContext.js
import React, { createContext, useState, useEffect, useCallback, useRef } from 'react'; // Додано useRef
// Імпортуємо лише setUnauthorizedCallback
import api, { setUnauthorizedCallback } from '../utils/api'; // ВИДАЛЕНО: setIsLoggingOut

export const UserContext = createContext();

export const UserProvider = ({ children }) => {
    // --- ОНОВЛЕНО: Початковий стан: ініціалізуємо з УСІХ даних localStorage, включаючи profile_picture_url, id, isAdmin ---
    const [user, setUser] = useState(() => {
        try { // Додано try/catch на випадок помилок парсингу localStorage
            const storedUserId = localStorage.getItem('userId');
            const storedUserName = localStorage.getItem('userName');
            const storedIsAdmin = localStorage.getItem('isAdmin') === 'true';
            const storedProfilePictureUrl = localStorage.getItem('userProfilePictureUrl'); // <-- Читаємо URL фото

            // Повертаємо повний об'єкт, якщо є мінімальні дані
            if (storedUserId && storedUserName !== null) { // Перевіряємо userName на null явно
                console.log('Context: Initializing user from localStorage:', { storedUserId, storedUserName, storedIsAdmin, storedProfilePictureUrl });
                return {
                    id: storedUserId,
                    name: storedUserName,
                    is_admin: storedIsAdmin,
                    profile_picture_url: storedProfilePictureUrl || null // Включаємо URL
                    // TODO: Можливо, додати інші поля, які можуть зберігатись у LS при логіні/верифікації
                };
            }
        } catch (e) {
            console.error("Context: Error reading user data from localStorage", e);
            // Очистити localStorage, якщо сталося помилка
            localStorage.removeItem('userId');
            localStorage.removeItem('userName');
            localStorage.removeItem('isAdmin');
            localStorage.removeItem('userProfilePictureUrl');
        }
        return null; // Якщо даних у localStorage немає або помилка
    });

    // isAdmin та userId тепер ПОВИННІ братися з об'єкта user, або оновлюватися разом з ним.
    // Краще брати їх з user, але якщо вони використовуються окремо, оновлюйте їх в setUser колбеках.
    // Залишаємо їх окремо, але будемо синхронізувати.
    const [isAdmin, setIsAdmin] = useState(() => localStorage.getItem('isAdmin') === 'true');
    const [userId, setUserId] = useState(() => localStorage.getItem('userId'));

    const [hasDevice, setHasDevice] = useState(false);
    const [loadingDevice, setLoadingDevice] = useState(false);
    const [loadingInitial, setLoadingInitial] = useState(true);
    const [error, setError] = useState(null);

    const isInitialCheckDone = useRef(false);

    // --- Функція перевірки наявності пристрою ---
    // Ця функція не має залежати від loadingDevice для свого useCallback,
    // оскільки вона сама керує цим станом. Вона залежить від `user`.
    const checkDeviceStatus = useCallback(async (currentUser) => {
        // Робимо перевірку лише якщо користувач виглядає залогіненим (присутній user об'єкт)
        // і ми ще не перевіряємо його пристрій
        // Використовуємо аргумент currentUser, який передається (або беремо поточний user зі scope)
        if (!currentUser) {
            console.log('Context: checkDeviceStatus skipped - user is null.');
            setHasDevice(false); // Якщо user = null, пристрою точно немає
            // setLoadingDevice(false); // Переконаємося, що loadingDevice також false
            return; // Не виконуємо запит
        }
        // Перевіряємо loadingDevice тут, щоб уникнути повторних викликів, якщо вони якось накладаються
        if (loadingDevice) {
            console.log('Context: checkDeviceStatus skipped - already checking.');
            return;
        }


        setLoadingDevice(true);
        console.log('Context: Performing device status check...');
        try {
            const devicesRes = await api.get('/user-iot-devices');
            const userHasDevice = devicesRes.data && devicesRes.data.length > 0;
            setHasDevice(userHasDevice);
            console.log(`Context: Device check successful. Has device: ${userHasDevice}.`);
            // setError(null);

        } catch (err) {
            console.error('Context: Failed to fetch user devices:', err);
            // Перехоплювач обробить 401 і викличе logoutUser.
            // Для інших помилок, вважаємо, що пристрій недоступний у цей момент.
            if (err.response?.status !== 401) {
                setHasDevice(false); // Встановлюємо false при інших помилках
                // setError(err); // Можна встановити помилку
            }
        } finally {
            setLoadingDevice(false);
        }
    }, [user]); // Залежність тільки від user

    // --- Початкова перевірка при монтуванні ---
    // Використовуємо реф, щоб переконатися, що цей ефект виконується лише один раз
    useEffect(() => {
        if (isInitialCheckDone.current) {
            console.log('Context: Initial check already done, skipping useEffect.');
            return; // Виходимо, якщо перевірка вже була
        }

        isInitialCheckDone.current = true; // Позначаємо, що перевірка почалася
        console.log('Context: Starting initial check useEffect.');

        const storedUserName = localStorage.getItem('userName');
        const storedProfilePictureUrl = localStorage.getItem('userProfilePictureUrl');
        const initialUser = storedUserName ? {
            name: storedUserName,
            profile_picture_url: storedProfilePictureUrl || null,
        } : null;

        // Встановлюємо початковий стан користувача та адміністратора з LS
        setUser(initialUser);
        setIsAdmin(localStorage.getItem('isAdmin') === 'true');


        // Якщо user об'єкт присутній (навіть з localStorage), запускаємо перевірку пристрою
        if (initialUser) {
            // Асинхронно викликаємо перевірку пристрою. checkDeviceStatus сама керує loadingDevice.
            // Передаємо initialUser, щоб checkDeviceStatus мала актуальне значення user.
            checkDeviceStatus(initialUser);
        } else {
            // Якщо user = null з самого початку, пристрою точно немає
            setHasDevice(false);
        }

        // Початкове завантаження завершено після ініціалізації з LS та запуску (або пропуску) перевірки пристрою
        // loadingDevice може ще бути true, якщо checkDeviceStatus запущено.
        setLoadingInitial(false);
        console.log('Context: Initial check useEffect finished.');

    }, [checkDeviceStatus]); // Залежність від checkDeviceStatus


    // --- Логіка Виходу ---
    const logoutUser = useCallback(async () => {
        console.log('Context: logoutUser called.');

        // Опціонально: викликаємо endpoint для виходу на бекенді
        // !!! ВАЖЛИВО: цей endpoint НЕ ПОВИНЕН повертати 401, який знову спрацює в перехоплювачі,
        // коли сесія вже недійсна. Повинен повертати 200 або інший статус.
        try {
            console.log('Context: Attempting backend /auth/logout call (optional)...');
            // await api.post('/auth/logout'); // Розкоментуйте, якщо маєте такий endpoint
            console.log('Context: Backend logout call finished (or skipped).');
        } catch (err) {
            console.error('Context: Failed to call backend logout API:', err);
        } finally {
            // --- Очищення стану та localStorage ---
            console.log('Context: Performing local logout cleanup.');
            setUser(null);
            setIsAdmin(false);
            setHasDevice(false); // При виході пристрою точно немає
            setLoadingDevice(false); // При виході скасовуємо будь-яке завантаження пристрою
            localStorage.removeItem('userName');
            localStorage.removeItem('isAdmin');
            localStorage.removeItem('userProfilePictureUrl'); // <-- Видаляємо URL фото з localStorage

            // localStorage.removeItem('token'); // Розкоментуйте, якщо зберігаєте токен у localStorage

            // *** Немає isLoggingOut прапорця для скидання ***

            // *** ВАЖЛИВО: Перенаправлення на /login сторінку відбувається в App (або роутері),
            // який спостерігає за зміною стану 'user' на null. ***
        }
    }, []);

    // --- Зв'язуємо logoutUser з перехоплювачем API ---
    useEffect(() => {
        console.log('Context: Setting API unauthorized callback.');
        setUnauthorizedCallback(logoutUser);

        return () => {
            console.log('Context: Clearing API unauthorized callback.');
            setUnauthorizedCallback(null);
        };
    }, [logoutUser]);


    // --- Функція для обробки успіху логіну (викликається з LoginPage) ---
    const handleLoginSuccess = useCallback((userDataFromLogin) => {
        console.log('Context: handleLoginSuccess called.');
        // Оновлюємо стан користувача на основі даних з логіну
        setUser({ name: userDataFromLogin.name });
        setIsAdmin(userDataFromLogin.is_admin || false);
        // setHasDevice залишається false поки не завершиться checkDeviceStatus
        setError(null); // Очищаємо помилки

        // *** ЗАПУСКАЄМО ПЕРЕВІРКУ ПРИСТРОЮ ПІСЛЯ УСПІШНОГО ЛОГІНУ ***
        // Оскільки setUser є асинхронним, передаємо актуальні дані user в checkDeviceStatus
        // або покладаємося на те, що useCallback має доступ до актуального user зі scope.
        // Передача аргумента currentUser в checkDeviceStatus є безпечнішою.
        checkDeviceStatus({ name: userDataFromLogin.name }); // Передаємо актуальний user об'єкт

        // localStorage вже збережено в LoginPage.

    }, [checkDeviceStatus]); // Залежність від checkDeviceStatus


    // Значення контексту, що передається
    const contextValue = {
        user, // null або { name: string }
        isAdmin, // boolean
        hasDevice, // boolean (статус пристрою)
        loadingInitial, // boolean (чи йде початкове завантаження контексту)
        loadingDevice, // boolean (чи йде перевірка пристрою)
        error, // any
        logoutUser, // Функція для виходу
        handleLoginSuccess, // Функція для LoginPage при успішному логіні
        checkDeviceStatus, // Може знадобитись деінде для оновлення статусу пристрою
    };

    return (
        <UserContext.Provider value={contextValue}>
            {children}
        </UserContext.Provider>
    );
};
