// src/pages/LoginPage.jsx
import React, { useState, useContext } from 'react';
import api from '../utils/api';
import { useNavigate } from 'react-router-dom';
import { auth, googleProvider } from '../firebase'; // Припускаємо, що Firebase використовується для Google Auth
import { signInWithPopup } from 'firebase/auth';
import { UserContext } from '../contexts/UserContext'; // Переконайтесь, що це правильний шлях до UserContext
import AuthForm from '../components/AuthForm'; // Переконайтесь, що це правильний шлях до AuthForm


export default function LoginPage() {
    const navigate = useNavigate();
    const { user, handleLoginSuccess } = useContext(UserContext);

    const [loginInProgress, setLoginInProgress] = useState(false);
    const [formError, setFormError] = useState(null);

    // Перенаправлення, якщо користувач вже залогінений
    if (user) {
        console.log('LoginPage: User is already logged in, navigating to /.');
        navigate('/', { replace: true });
        return null;
    }


    // --- Логіка стандартного логіну (email/password) ---
    const handleLogin = async ({ email, password }) => {
        setLoginInProgress(true);
        setFormError(null);

        console.log('LoginPage: Attempting password login with data:', { email, password });

        try {
            const res = await api.post('/auth/login/password', { email, password }, {
                withCredentials: true
            });
            console.log('LoginPage: Password login success. Response received.');

            const { user } = res.data; // Отримуємо об'єкт user з відповіді

            // --- ЗБЕРІГАННЯ В localStorage ---
            localStorage.setItem('userName', user.name);
            localStorage.setItem('isAdmin', user.is_admin ? 'true' : 'false');
            localStorage.setItem('userId', user.id);
            // !!! ЗБЕРІГАЄМО URL ФОТО ПРОФІЛЮ, ЯКЩО ВІН Є У ВІДПОВІДІ !!!
            if (user.profile_picture_url) { // Перевіряємо наявність поля
                localStorage.setItem('profilePicture', user.profile_picture_url);
                console.log('LoginPage: profilePicture saved to localStorage:', user.profile_picture_url);
            } else {
                // Якщо поле не прийшло або null, видаляємо його з localStorage (на випадок, якщо було раніше)
                localStorage.removeItem('profilePicture');
                console.log('LoginPage: No profile picture URL received, removed from localStorage.');
            }
            // --- КІНЕЦЬ ЗБЕРІГАННЯ ---


            // --- ПОВІДОМЛЯЄМО КОНТЕКСТ ПРО УСПІХ ---
            // Передаємо ВЕСЬ об'єкт user (з profile_picture_url) в контекст
            handleLoginSuccess(user);

        } catch (err) {
            console.error('LoginPage: Password login error:', err.response?.data || err.message);
            const errorMessage = err.response?.data?.message || 'Помилка входу';
            setFormError(errorMessage);
            alert('Помилка входу: ' + errorMessage);
        } finally {
            setLoginInProgress(false);
        }
    };

    // --- Логіка логіну через Google ---
    const handleGoogleLogin = async () => {
        setLoginInProgress(true);
        setFormError(null);

        console.log('LoginPage: Initiating Google sign-in popup.');
        try {
            const result = await signInWithPopup(auth, googleProvider);
            const token = await result.user.getIdToken();
            console.log('LoginPage: Firebase Google auth success. Token obtained.');

            console.log('LoginPage: Sending Google token to backend /auth/login/google...');
            const res = await api.post('/auth/login/google', { token }, { withCredentials: true });
            console.log('LoginPage: Backend Google login success. Response received.', res.data);

            const { user } = res.data; // Отримуємо об'єкт user з відповіді

            // --- ЗБЕРІГАННЯ В localStorage ---
            localStorage.setItem('userName', user.name);
            localStorage.setItem('isAdmin', user.is_admin ? 'true' : 'false');
            localStorage.setItem('userId', user.id);
            // !!! ЗБЕРІГАЄМО URL ФОТО ПРОФІЛЮ, ЯКЩО ВІН Є У ВІДПОВІДІ !!!
            if (user.profile_picture_url) {
                localStorage.setItem('profilePicture', user.profile_picture_url);
                console.log('LoginPage: profilePicture saved to localStorage:', user.profile_picture_url);
            } else {
                localStorage.removeItem('profilePicture');
                console.log('LoginPage: No profile picture URL received, removed from localStorage.');
            }

            handleLoginSuccess(user);

        } catch (error) {
            console.error('LoginPage: Google login error:', error);
            const errorMessage = error.message || 'Помилка авторизації через Google';
            setFormError(errorMessage);
            alert('Помилка авторизації через Google: ' + errorMessage);
        } finally {
            setLoginInProgress(false);
        }
    };


    return (
        <>
            {formError && <div style={{ color: 'red', marginBottom: '10px', textAlign: 'center' }}>{formError}</div>}

            <AuthForm
                type="login"
                onSubmit={handleLogin}
                onGoogleLogin={handleGoogleLogin}
                isSubmitting={loginInProgress}
            />
        </>
    );
}
