import React from 'react';
import {useNavigate} from "react-router-dom";

const CreateField = ({ closeCreateField }) => {
    const navigate = useNavigate(); // використання useNavigate

    const handleCancel = () => {
        // Перехід на попередню сторінку в історії браузера
        navigate(-1);
    };
    return (
        <div className="h-screen w-full flex flex-1 flex-wrap">
            <div className="p-6">
                <h5 className="font-bold text-black">Створити нове поле</h5>
                {/* Тут можна додати форму або інші елементи для створення поля */}
                <form>
                    <label className="block mb-2">
                        Назва поля:
                        <input
                            type="text"
                            className="w-full border border-gray-300 p-2 rounded-lg"
                        />
                    </label>
                    <label className="block mb-2">
                        Опис:
                        <textarea
                            className="w-full border border-gray-300 p-2 rounded-lg"
                            rows="4"
                        ></textarea>
                    </label>
                    <button
                        type="submit"
                        className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-full mt-4"
                    >
                        Створити поле
                    </button>
                </form>
                <button
                    onClick={handleCancel}
                    className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-full font-semibold mt-4"
                >
                    Скасувати
                </button>
            </div>
        </div>
    );
};

export default CreateField;
