import React from 'react';

const CreateField = () => {
    const handleCancel = () => {
        setIsCreatingField(false); // Закриває форму, коли натискається скасування
    };
    return (
        <div className="h-screen w-full flex-1">
            <div className="p-6">
                <h5 className="font-bold text-black">Створити нове поле</h5>
                {/* Тут можна додати форму або інші елементи для створення поля */}
                <form>
                    <label className="block mb-2">
                        Назва поля:
                        <input
                            type="text"
                            className="w-full border border-gray-300 p-2 rounded-lg"
                        />
                    </label>
                    <label className="block mb-2">
                        Опис:
                        <textarea
                            className="w-full border border-gray-300 p-2 rounded-lg"
                            rows="4"
                        ></textarea>
                    </label>
                    <button
                        type="submit"
                        className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-full mt-4"
                    >
                        Створити поле
                    </button>
                    
                </form>
            </div>
        </div>
    );
};

export default CreateField;
