import React, { createContext, useState, useEffect } from 'react';
import api from '../utils/api';

export const UserContext = createContext();

export const UserProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [isAdmin, setIsAdmin] = useState(false);
    const [hasDevice, setHasDevice] = useState(false);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                // Симулюємо дані з localStorage
                const name = localStorage.getItem('userName');
                const admin = localStorage.getItem('isAdmin') === 'true';

                setUser({ name });
                setIsAdmin(admin);

                // Запит на перевірку девайсу
                const res = await api.get('/user-iot-devices', {
                    withCredentials: true
                });

                if (res.data && res.data.length > 0) {
                    setHasDevice(true);
                } else {
                    setHasDevice(false);
                }

            } catch (err) {
                console.error('Помилка отримання даних користувача або пристроїв', err);
            } finally {
                setLoading(false);
            }
        };

        fetchUserData();
    }, []);

    return (
        <UserContext.Provider value={{ user, isAdmin, hasDevice, loading }}>
            {children}
        </UserContext.Provider>
    );
};
