import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './styles/App.css';
import LoginModal from "./components/LoginModal";
import RegisterModal from "./components/RegisterModal";
import Registration from './components/Registration'; // Сторінка реєстрації

function App() {
  return (
      <Router>
        <div className="App">
          <header className="animal-card">
            <h1 className="text-4xl font-bold">Вітаємо у SoilScout</h1>
            <p className="mt-4">
              Це демо-додаток для тестування React і Tailwind CSS.
            </p>
            <h1 className="text-4xl font-bold text-blue-500">Тест Tailwind</h1>

            <div className="flex space-x-4 mt-6">
              {/* Кнопки для відкриття модальних вікон */}
              <LoginModal/>
              <RegisterModal/>
            </div>
            <Link
                to="/registration"
                className="px-6 py-3 mt-6 bg-blue-500 text-white rounded shadow hover:bg-blue-600"
            >
              Перейти до реєстрації
            </Link>
          </header>
          <Routes>
            <Route path="/registration" element={<Registration />} />
          </Routes>
        </div>
      </Router>
  );
}

export default App;
