import React, { useEffect } from 'react';
import { useTranslation } from 'react-i18next';

export default function LanguageSwitcher() {
    // Отримуємо об'єкт i18n через хук
    const { i18n } = useTranslation();

    // Визначаємо, чи є поточною мовою англійська. Ця змінна
    // автоматично переобчислюється, коли i18n.language змінюється
    // (завдяки useTranslation, який викликає ререндер)
    const isEnglish = i18n.language === 'en';

    const toggleLanguage = () => {
        // Визначаємо нову мову для перемикання
        // Якщо зараз англійська ('en'), нова буде українська ('uk').
        // В іншому випадку (якщо 'uk'), нова буде англійська ('en').
        const newLang = i18n.language === 'en' ? 'uk' : 'en';

        // Змінюємо мову в i18n та оновлюємо в localStorage
        i18n.changeLanguage(newLang);
        localStorage.setItem('language', newLang);
        // Завдяки useTranslation, зміна i18n.language викличе ререндер,
        // і isEnglish, а отже і візуальне положення світчера, оновляться.
    };

    useEffect(() => {
        // Цей ефект виконується ОДИН РАЗ при першому монтуванні компонента.
        // Його мета - встановити початкову мову на основі localStorage або за замовчуванням.

        const storedLang = localStorage.getItem('language');
        // Встановлюємо початкову мову: якщо в localStorage є збережена мова, використовуємо її,
        // інакше за замовчуванням встановлюємо АНГЛІЙСЬКУ ('en').
        const initialLang = storedLang || 'en';

        // **Форсуємо** встановлення цієї початкової мови в i18n.
        // Це може допомогти, якщо i18n спочатку ініціалізується з іншою мовою
        // до того, як цей ефект встигає спрацювати.
        i18n.changeLanguage(initialLang);
        // Також переконаємося, що localStorage відповідає встановленій початковій мові
        localStorage.setItem('language', initialLang);

        // Порожній масив залежностей [] означає, що ефект спрацює тільки один раз при монтуванні.
        // Нам не потрібно додавати i18n сюди, оскільки ми не залежимо від його змін
        // *після* початкового встановлення в цьому ефекті.
    }, []);


    return (
        // Цей div є контейнером для кнопки та тексту, вирівнюючи їх по центру
        <div className="flex flex-col items-center">
            <button
                onClick={toggleLanguage}
                // Ширина кнопки та інші стилі
                className="flex items-center w-12 h-6 bg-gray-300 rounded-full px-1 border border-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-colors duration-300 ease-in-out"
            >
                <span
                    // Кружечок перемикача.
                    // Положення залежить від isEnglish, який вираховується з i18n.language.
                    // translate-x-0 (ліворуч) для isEnglish (мови 'en').
                    // translate-x-6 (праворуч) для !isEnglish (мови 'uk').
                    className={`w-4 h-4 bg-white rounded-full shadow-md transform duration-300 ease-in-out ${isEnglish ? 'translate-x-0' : 'translate-x-6'}`}
                ></span>
            </button>
            <div className="text-xs text-center mt-1">
                {/* Відображаємо поточне скорочення мови великими літерами */}
                {i18n.language.toUpperCase()}
            </div>
        </div>
    );
}
