// contexts/UserContext.js
import React, { createContext, useState, useEffect, useCallback, useRef } from 'react';
import api, { setUnauthorizedCallback } from '../utils/api';

export const UserContext = createContext();

export const UserProvider = ({ children }) => {
    // --- ВИПРАВЛЕННЯ: Початковий стан: ініціалізуємо user з усіх даних localStorage ---
    const [user, setUser] = useState(() => {
        const storedUserId = localStorage.getItem('userId');
        const storedUserName = localStorage.getItem('userName');
        const storedIsAdmin = localStorage.getItem('isAdmin') === 'true';
        const storedProfilePictureUrl = localStorage.getItem('userProfilePictureUrl'); // Отримуємо URL фото

        // Конструюємо об'єкт користувача, якщо є ID та Ім'я
        if (storedUserId && storedUserName) {
            return {
                id: storedUserId,
                name: storedUserName,
                is_admin: storedIsAdmin,
                profile_picture_url: storedProfilePictureUrl || null // Включаємо URL фото
            };
        }
        return null; // Користувача немає в localStorage
    });

    // ВИПРАВЛЕННЯ: Ініціалізуємо isAdmin та userId з localStorage
    const [isAdmin, setIsAdmin] = useState(() => localStorage.getItem('isAdmin') === 'true');
    const [userId, setUserId] = useState(() => localStorage.getItem('userId'));


    const [hasDevice, setHasDevice] = useState(false);
    const [loadingDevice, setLoadingDevice] = useState(false);
    const [loadingInitial, setLoadingInitial] = useState(true);
    const [error, setError] = useState(null);

    const isInitialCheckDone = useRef(false);


    // --- Функція перевірки наявності пристрою (залежить від user.id) ---
    // Використовуємо аргумент currentUser для доступу до актуального user об'єкта з id
    const checkDeviceStatus = useCallback(async (currentUser) => {
        const userToCheck = currentUser || user; // Використовуємо переданий аргумент або поточний стан

        if (!userToCheck || !userToCheck.id) { // Перевіряємо наявність об'єкта користувача та його ID
            console.log('Context: checkDeviceStatus skipped - user or user ID is null.');
            setHasDevice(false);
            return;
        }

        if (loadingDevice) {
            console.log('Context: checkDeviceStatus skipped - already checking.');
            return;
        }


        setLoadingDevice(true);
        console.log('Context: Performing device status check for user ID:', userToCheck.id);
        try {
            const devicesRes = await api.get('/user-iot-devices');
            const userHasDevice = devicesRes.data && devicesRes.data.length > 0;
            setHasDevice(userHasDevice);
            console.log(`Context: Device check successful. Has device: ${userHasDevice}.`);

        } catch (err) {
            console.error('Context: Failed to fetch user devices:', err);
            // Перехоплювач обробить 401, для інших помилок вважаємо, що пристрою немає
            if (err.response?.status !== 401) {
                setHasDevice(false);
            }
        } finally {
            setLoadingDevice(false);
        }
    }, [user, loadingDevice]);


    // --- Початкова перевірка при монтуванні (Верифікація сесії та отримання свіжих даних) ---
    // ВИПРАВЛЕННЯ: Цей ефект тепер запускає верифікацію, яка оновить стан user свіжими даними
    useEffect(() => {
        if (isInitialCheckDone.current) {
            console.log('Context: Initial check already done, skipping useEffect.');
            return;
        }

        isInitialCheckDone.current = true;
        console.log('Context: Starting initial check useEffect.');

        // Стан user вже ініціалізовано з localStorage перед цим ефектом
        const initialUserFromState = user;

        // Функція для верифікації сесії та отримання свіжих даних користувача
        const verifyAuthAndFetchUserData = async () => {
            try {
                // Викликаємо ендпоінт /users/me (або /auth/me), який має повернути ПОВНИЙ об'єкт користувача
                const res = await api.get('/users/me', { withCredentials: true }); // Використовуємо ендпоінт з userController
                const fetchedUser = res.data; // Припускаємо, що /users/me повертає об'єкт користувача верхнього рівня, як у Profile.jsx
                console.log('UserContext: Backend auth verification successful, user data fetched:', fetchedUser);

                if (fetchedUser && fetchedUser.id) { // Перевіряємо, що користувач отриманий і має ID
                    // !!! ВИПРАВЛЕННЯ: ОНОВЛЮЄМО СТАН КОРИСТУВАЧА ПОВНИМ ОБ'ЄКТОМ З БЕКЕНДУ !!!
                    setUser(fetchedUser); // Це оновить id, name, is_admin, profile_picture_url

                    // ОНОВЛЮЄМО localStorage СВІЖИМИ ДАНИМИ (для стійкості між сесіями)
                    localStorage.setItem('userId', fetchedUser.id);
                    localStorage.setItem('userName', fetchedUser.name);
                    localStorage.setItem('isAdmin', fetchedUser.is_admin ? 'true' : 'false');
                    if (fetchedUser.profile_picture_url) {
                        localStorage.setItem('userProfilePictureUrl', fetchedUser.profile_picture_url);
                        console.log('UserContext: Fresh profile picture URL saved to localStorage:', fetchedUser.profile_picture_url);
                    } else {
                        localStorage.removeItem('userProfilePictureUrl');
                        console.log('UserContext: No fresh profile picture URL received, removed from localStorage.');
                    }

                    // Оновлюємо isAdmin та userId стани на основі свіжих даних
                    setIsAdmin(fetchedUser.is_admin || false);
                    setUserId(fetchedUser.id);


                    // Запускаємо перевірку пристрою з актуальним об'єктом fetchedUser
                    checkDeviceStatus(fetchedUser);

                } else {
                    console.log('UserContext: Backend auth verification failed or returned no user, logging out locally.');
                    // Якщо бекенд повернув 200, але без користувача, або логіка була інша
                    logoutUser();
                }
            } catch (error) {
                console.error('UserContext: Auth verification API error:', error);
                // Перехоплювач обробить 401 помилку і викличе logoutUser.
                // Для інших помилок, можливо, також виконуємо локальний вихід.
                // logoutUser(); // Опціонально: виконувати локальний вихід при будь-якій помилці верифікації
            } finally {
                setLoadingInitial(false); // Завантаження завершено після спроби верифікації
                console.log('Context: Initial auth verification process finished.');
            }
        };

        // Запускаємо процес верифікації, якщо з localStorage були якісь дані користувача
        if (initialUserFromState) {
            console.log('UserContext: Initial user data found in localStorage, starting verification.');
            verifyAuthAndFetchUserData();
        } else {
            // Немає початкових даних користувача, завантаження завершено.
            console.log('UserContext: No initial user data, skipping verification.');
            setLoadingInitial(false);
            setHasDevice(false);
        }


    }, [checkDeviceStatus, logoutUser]); // Додано checkDeviceStatus та logoutUser до залежностей

    // ... Логіка Виходу logoutUser (залишається без змін, але перевірте видалення userProfilePictureUrl) ...
    const logoutUser = useCallback(async () => {
        console.log('Context: logoutUser called.');
        try {
            console.log('Context: Attempting backend /auth/logout call (optional)...');
            await api.post('/auth/logout'); // Викликаємо ендпоінт виходу
            console.log('Context: Backend logout call finished.');
        } catch (err) {
            console.error('Context: Failed to call backend logout API:', err);
        } finally {
            console.log('Context: Performing local logout cleanup.');
            setUser(null);
            setIsAdmin(false);
            setUserId(null);
            setHasDevice(false);
            setLoadingDevice(false);
            setError(null);

            // ВИПРАВЛЕННЯ: Видаляємо URL фото з localStorage під час виходу
            localStorage.removeItem('userId');
            localStorage.removeItem('userName');
            localStorage.removeItem('isAdmin');
            localStorage.removeItem('userProfilePictureUrl'); // <-- ВИДАЛИТИ ЦЕЙ КЛЮЧ

            console.log('Context: Local logout cleanup finished.');
        }
    }, []);


    // ... Зв'язуємо logoutUser з перехоплювачем API (useEffect) (залишається без змін) ...
    useEffect(() => {
        console.log('Context: Setting API unauthorized callback.');
        setUnauthorizedCallback(logoutUser);
        return () => {
            console.log('Context: Clearing API unauthorized callback.');
            setUnauthorizedCallback(null);
        };
    }, [logoutUser]);


    // --- ВИПРАВЛЕННЯ: Функція для обробки успіху логіну (викликається з LoginPage) ---
    const handleLoginSuccess = useCallback((userDataFromLogin) => {
        console.log('Context: handleLoginSuccess called with userData:', userDataFromLogin);
        // !!! ВИПРАВЛЕННЯ: ОНОВЛЮЄМО СТАН КОРИСТУВАЧА ПОВНИМ ОБ'ЄКТОМ, ОТРИМАНИМ З LoginPage !!!
        // Цей об'єкт userDataFromLogin має містити id, name, is_admin, ТА profile_picture_url
        setUser(userDataFromLogin);

        // Оновлюємо isAdmin та userId стани на основі нового об'єкта user
        setIsAdmin(userDataFromLogin.is_admin || false);
        setUserId(userDataFromLogin.id || null); // Використовуємо id з об'єкта

        setError(null); // Очищаємо помилки

        // localStorage вже збережено в LoginPage перед цим викликом.

        // *** ЗАПУСКАЄМО ПЕРЕВІРКУ СТАТУСУ ПРИСТРОЮ ПІСЛЯ УСПІШНОГО ЛОГІНУ ***
        checkDeviceStatus(userDataFromLogin); // Передаємо актуальний user об'єкт

    }, [checkDeviceStatus]); // Залежність від checkDeviceStatus


    // Значення контексту, що передається
    const contextValue = {
        user, // !!! Надаємо ПОВНИЙ об'єкт користувача !!!
        isAdmin, // boolean
        userId, // string або null
        hasDevice, // boolean
        loadingInitial, // boolean
        loadingDevice, // boolean
        error, // any
        logoutUser, // Функція для виходу
        handleLoginSuccess, // Функція для LoginPage при успішному логіні
        // checkDeviceStatus, // Можна прибрати, якщо не використовується зовнішньо
    };

    return (
        <UserContext.Provider value={contextValue}>
            {children}
        </UserContext.Provider>
    );
};
