import React from 'react';
import AuthForm from '../components/AuthForm';
import api from '../utils/api';
import { useNavigate } from 'react-router-dom';

export default function LoginPage() {
    const navigate = useNavigate();

    const handleLogin = async (email, password) => {
        try {
            // Встановлюємо withCredentials: true для підтримки куків
            const res = await api.post('/auth/login/password', { email, password }, {
                withCredentials: true  // Це важливо!
            });
            console.log('Login success:', res.data);
            navigate('/dashboard');
        } catch (err) {
            alert('Помилка входу: ' + err.response?.data?.message || err.message);
        }
    };

    return <AuthForm type="login" onSubmit={handleLogin} />;
}
