import { useState } from "react";
import DashboardContent from "./DashboardContent";
import MainContent from "./MainContent";
import CreateField from "./CreateField";  // Компонент створення поля

const HomePage = () => {
    const [isCreatingField, setIsCreatingField] = useState(false);

    const handleAddField = () => {
        setIsCreatingField(true); // Показуємо компонент створення поля
    };

    return (
        <div className="flex">
            <DashboardContent />
            <div className="w-full flex-1">
                {isCreatingField ? (
                    <CreateField />  // Відображаємо компонент створення поля
                ) : (
                    <MainContent />  // Відображаємо основний контент
                )}
                <button
                    onClick={handleAddField}
                    className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-full font-semibold mt-4"
                >
                    Додати поле
                </button>
            </div>
        </div>
    );
}

export default HomePage;
