import React, { createContext, useContext, useState } from 'react';

// Створюємо контекст для створення поля
const FieldContext = createContext();

// Провайдер контексту, що буде надавати доступ до стану та функцій
export const FieldProvider = ({ children }) => {
    const [isCreatingField, setIsCreatingField] = useState(false);

    return (
        <FieldContext.Provider value={{ isCreatingField, setIsCreatingField }}>
            {children}
        </FieldContext.Provider>
    );
};

// Хук для використання контексту в інших компонентах
export const useFieldContext = () => {
    return useContext(FieldContext);
};
