import { useState, useRef, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import UserMenu from "./UserMenu";
import { useFieldContext } from '../contexts/FieldContext';

const langData = {
    ua: {
        addField: "Додати поле",
        sortBy: {
            name: "Назва",
            area: "Розмір",
            created_at: "Дата створення"
        },
        filter: {
            all: "Усі",
            active: "Активні",
            inactive: "Неактивні"
        },
        search: "Пошук поля..."
    },
    en: {
        addField: "Add Field",
        sortBy: {
            name: "Name",
            area: "Area",
            created_at: "Created At"
        },
        filter: {
            all: "All",
            active: "Active",
            inactive: "Inactive"
        },
        search: "Search field..."
    }
};

export default function Navbar({ setIsCreatingField }) {
    const {
        sortBy, setSortBy,
        sortOrder, setSortOrder,
        filter, setFilter,
        setSearchTerm
    } = useFieldContext();

    const [language, setLanguage] = useState("ua");
    const [isFilterMenuOpen, setIsFilterMenuOpen] = useState(false);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const filterRef = useRef(null);
    const dropdownRef = useRef(null);
    const navigate = useNavigate();

    const handleAddField = () => navigate('/create-field');

    const toggleLanguage = () => {
        setLanguage(prev => (prev === "ua" ? "en" : "ua"));
    };

    const handleSortOrderToggle = () => {
        const newOrder = sortOrder === "asc" ? "desc" : "asc";
        setSortOrder(newOrder);
    };

    const handleSortByChange = (newSortBy) => {
        if (newSortBy !== sortBy) {
            setSortBy(newSortBy);
            setIsDropdownOpen(false);
        }
    };

    const handleFilterChange = (newFilter) => {
        setFilter(newFilter);
        setIsFilterMenuOpen(false);
    };

    useEffect(() => {
        const handleClickOutside = (e) => {
            if (filterRef.current && !filterRef.current.contains(e.target)) {
                setIsFilterMenuOpen(false);
            }
            if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
                setIsDropdownOpen(false);
            }
        };
        document.addEventListener("mousedown", handleClickOutside);
        return () => document.removeEventListener("mousedown", handleClickOutside);
    }, []);

    const t = langData[language]; // Поточна мова

    return (
        <div className="h-40 lg:h-20 w-full flex flex-wrap">
            <nav className="bg-gray-200 w-full lg:max-w-sm flex items-center border-b border-gray-300 order-2 lg:order-1">
                <div className="flex w-full p-3 gap-2 items-center">
                    <button
                        onClick={handleAddField}
                        className="bg-green-500 hover:bg-green-600 text-white px-2 py-2 rounded-full font-semibold"
                    >
                        <i className="fas fa-plus mr-1" />
                        {t.addField}
                    </button>

                    <div className="flex items-center gap-2 relative" ref={dropdownRef}>
                        <div className="relative flex items-center gap-1">
                            <button
                                onClick={() => setIsDropdownOpen(prev => !prev)}
                                className="bg-gray-300 hover:bg-gray-400 text-gray-700 px-2 py-2 rounded-full"
                            >
                                {t.sortBy[sortBy]}
                            </button>
                            <button
                                onClick={handleSortOrderToggle}
                                className="bg-gray-300 hover:bg-gray-400 text-gray-700 px-2 py-2 rounded-full"
                            >
                                {sortOrder === "asc" ? (
                                    <i className="fas fa-arrow-up" />
                                ) : (
                                    <i className="fas fa-arrow-down" />
                                )}
                            </button>
                        </div>

                        {isDropdownOpen && (
                            <div className="absolute bg-white shadow-md rounded-lg mt-1 p-2 w-48 border top-10 z-50">
                                <ul>
                                    {["name", "area", "created_at"].map(key => (
                                        <li key={key}>
                                            <button
                                                onClick={() => handleSortByChange(key)}
                                                className="w-full text-left px-4 py-2 hover:bg-gray-200"
                                            >
                                                {t.sortBy[key]}
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        )}
                    </div>

                    <div className="relative ml-auto" ref={filterRef}>
                        <button
                            onClick={() => setIsFilterMenuOpen(prev => !prev)}
                            className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-2 rounded-full"
                            title="Фільтрувати"
                        >
                            <i className="fas fa-filter" />
                        </button>
                        {isFilterMenuOpen && (
                            <div className="absolute bg-white shadow-md rounded-lg mt-2 p-2 w-48 border top-10 z-50">
                                <ul>
                                    {["all", "active", "inactive"].map(key => (
                                        <li key={key}>
                                            <button
                                                onClick={() => handleFilterChange(key)}
                                                className="w-full text-left px-4 py-2 hover:bg-gray-200"
                                            >
                                                {t.filter[key]}
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        )}
                    </div>
                </div>
            </nav>

            <nav className="bg-gray-100 w-auto flex-1 border-b border-gray-300 order-1 lg:order-2">
                <div className="flex h-full justify-between items-center">
                    <div className="relative w-full max-w-3xl px-6 py-2">
                        <div className="absolute h-10 mt-1 left-0 top-0 flex items-center pl-10 pt-4">
                            <i className="fas fa-search text-gray-600" />
                        </div>
                        <input
                            type="search"
                            placeholder={t.search}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="block w-full bg-gray-200 text-gray-700 font-bold rounded-full pl-12 pr-4 py-3 focus:outline-none focus:bg-white focus:shadow-md"
                        />
                    </div>

                    {/* Перемикач мови */}
                    <div className="flex items-center pr-4">
                        <div className="flex items-center">
                            <span className="text-sm font-medium pr-1">UA</span>
                            <label className="relative inline-flex items-center cursor-pointer">
                                <input
                                    type="checkbox"
                                    value=""
                                    className="sr-only peer"
                                    checked={language === "en"}
                                    onChange={toggleLanguage}
                                />
                                <div className="w-11 h-6 bg-gray-300 peer-focus:outline-none peer-focus:ring-2 peer-focus:ring-blue-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-0.5 after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all" />
                            </label>
                            <span className="text-sm font-medium pl-1">EN</span>
                        </div>
                    </div>

                    <UserMenu />
                </div>
            </nav>
        </div>
    );
}
