import React, { useCallback, useRef, useState } from "react";
import { GoogleMap, LoadScript, Polygon } from "@react-google-maps/api";

const containerStyle = {
    width: "100%",
    height: "500px",
};

const center = {
    lat: 48.3794, // Україна, можеш змінити на координати поля
    lng: 31.1656,
};

const MapPolygonPicker = ({ onPolygonComplete }) => {
    const [paths, setPaths] = useState([]);
    const mapRef = useRef(null);

    const handleClick = useCallback((event) => {
        const newPoint = {
            lat: event.latLng.lat(),
            lng: event.latLng.lng(),
        };
        setPaths((prev) => [...prev, newPoint]);
    }, []);

    const handleComplete = () => {
        if (onPolygonComplete) onPolygonComplete(paths);
    };

    return (
        <LoadScript googleMapsApiKey={import.meta.env.VITE_GOOGLE_MAPS_API_KEY}>
            <GoogleMap
                mapContainerStyle={containerStyle}
                center={center}
                zoom={6}
                onClick={handleClick}
                onLoad={(map) => (mapRef.current = map)}
            >
                {paths.length > 0 && (
                    <Polygon
                        path={paths}
                        options={{
                            fillColor: "#00FF00",
                            fillOpacity: 0.3,
                            strokeColor: "#00AA00",
                            strokeOpacity: 1,
                            strokeWeight: 2,
                            editable: true,
                            draggable: false,
                        }}
                    />
                )}
            </GoogleMap>
            <button className="mt-2 bg-green-500 text-white px-4 py-2 rounded" onClick={handleComplete}>
                Підтвердити полігон
            </button>
        </LoadScript>
    );
};

export default MapPolygonPicker;
