import { Navigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { jwtDecode } from 'jwt-decode';

// Отримати токен з cookie
const getCookie = (name) => {
    const cookieValue = document.cookie
        .split("; ")
        .find((row) => row.startsWith(name + "="));
    return cookieValue ? cookieValue.split("=")[1] : null;
};

// Перевірка, чи токен дійсний
const isTokenValid = (token) => {
    try {
        const decoded = jwtDecode(token);
        return decoded.exp * 1000 > Date.now();
    } catch (err) {
        return false;
    }
};

const PrivateRoute = ({ children }) => {
    const [auth, setAuth] = useState(null);  // Відстежуємо стан автентифікації

    useEffect(() => {
        const token = getCookie("auth_token");

        if (token && isTokenValid(token)) {
            setAuth(true);
        } else {
            // Очищення куки, якщо токен невалідний або відсутній
            document.cookie = "auth_token=; Max-Age=0; path=/";
            setAuth(false);
        }
    }, []);  // Виконується один раз при монтуванні компонента

    if (auth === null) return null; // Показати прелоадер або нічого, поки автентифікація не завершена

    return auth ? children : <Navigate to="/login" replace />;
};

export default PrivateRoute;
