import React, { useEffect, useState } from 'react';
import api from '../../utils/api';

const AdminUserDevices = () => {
    const [userDevices, setUserDevices] = useState([]);
    const [users, setUsers] = useState([]);
    const [iotDevices, setIotDevices] = useState([]);
    const [selectedUserId, setSelectedUserId] = useState('');
    const [selectedIotDeviceId, setSelectedIotDeviceId] = useState('');

    useEffect(() => {
        fetchUsers();
        fetchIotDevices();
        fetchUserDevices();
    }, []);

    const fetchUsers = async () => {
        try {
            const res = await api.get('/users', { withCredentials: true });
            setUsers(res.data);
        } catch (err) {
            console.error('Error fetching users:', err);
        }
    };

    const fetchIotDevices = async () => {
        try {
            const res = await api.get('/iot-devices', { withCredentials: true });
            setIotDevices(res.data);
        } catch (err) {
            console.error('Error fetching IoT devices:', err);
        }
    };

    const fetchUserDevices = async () => {
        try {
            const res = await api.get('/userIotDevices', { withCredentials: true });
            setUserDevices(res.data);
        } catch (err) {
            console.error('Error fetching user devices:', err);
        }
    };

    const handleBindIotDeviceToUser = async () => {
        try {
            await api.post(
                '/userIotDevices',
                {
                    user_id: selectedUserId,
                    iot_device_id: selectedIotDeviceId
                },
                { withCredentials: true }
            );
            fetchUserDevices();
        } catch (err) {
            console.error('Error binding device to user:', err);
        }
    };

    const handleUnbindIotDeviceFromUser = async (id) => {
        try {
            await api.delete(`/userIotDevices/${id}`, { withCredentials: true });
            fetchUserDevices();
        } catch (err) {
            console.error('Error unbinding device from user:', err);
        }
    };

    return (
        <div className="p-6">
            <h2 className="text-2xl font-bold mb-4">Управління пристроями користувачів</h2>

            <div className="mb-6">
                <select
                    value={selectedUserId}
                    onChange={(e) => setSelectedUserId(e.target.value)}
                    className="border px-4 py-2 mr-2"
                >
                    <option value="">Виберіть користувача</option>
                    {users.map((user) => (
                        <option key={user.id} value={user.id}>
                            {user.name}
                        </option>
                    ))}
                </select>

                <select
                    value={selectedIotDeviceId}
                    onChange={(e) => setSelectedIotDeviceId(e.target.value)}
                    className="border px-4 py-2 mr-2"
                >
                    <option value="">Виберіть пристрій</option>
                    {iotDevices.map((device) => (
                        <option key={device.id} value={device.id}>
                            {device.name}
                        </option>
                    ))}
                </select>

                <button
                    onClick={handleBindIotDeviceToUser}
                    className="bg-indigo-500 text-white px-4 py-2 rounded hover:bg-indigo-600"
                >
                    Прив'язати пристрій до користувача
                </button>
            </div>

            <table className="w-full table-auto border-collapse border border-gray-200">
                <thead>
                <tr className="bg-gray-100">
                    <th className="border px-4 py-2">ID</th>
                    <th className="border px-4 py-2">Користувач</th>
                    <th className="border px-4 py-2">Пристрій</th>
                    <th className="border px-4 py-2">Дії</th>
                </tr>
                </thead>
                <tbody>
                {userDevices.map((userDevice) => (
                    <tr key={userDevice.id}>
                        <td className="border px-4 py-2">{userDevice.id}</td>
                        <td className="border px-4 py-2">
                            {users.find((user) => user.id === userDevice.user_id)?.name}
                        </td>
                        <td className="border px-4 py-2">
                            {iotDevices.find((device) => device.id === userDevice.iot_device_id)?.name}
                        </td>
                        <td className="border px-4 py-2">
                            <button
                                onClick={() => handleUnbindIotDeviceFromUser(userDevice.id)}
                                className="bg-red-500 text-white px-2 py-1 rounded"
                            >
                                Відв'язати
                            </button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdminUserDevices;
