import { useState } from "react";
import Navbar from "../components/Navbar";
import MainContent from "../components/MainContent";
import CreateField from "../components/CreateField";
import DashboardContent from "../components/DashboardContent";

const HomePage = () => {
    const [isCreatingField, setIsCreatingField] = useState(false);

    const handleAddField = () => {
        setIsCreatingField(true); // Перемикаємо на створення поля
    };

    return (
        <div className="flex">
            <div className="w-full flex-1">

                {/* Додаємо кнопку "Додати поле" без навбару */}
                <button
                    onClick={handleAddField}
                    className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-full font-semibold"
                >
                    <i className="fas fa-plus mr-1" />
                    Додати поле
                </button>
                <DashboardContent />
                {/* Виведення відповідного контенту залежно від стану */}
                {isCreatingField ? (
                    <CreateField />  // Відображаємо компонент створення поля
                ) : (
                    <MainContent />  // Відображаємо основний контент
                )}
            </div>
        </div>
    );
}

export default HomePage;
