import React from 'react';
import { Mail, Phone, MapPin } from 'lucide-react';

const ContactPage = () => {
    return (
        <div className="p-6 space-y-8">
            <h1 className="text-4xl font-bold text-center mb-8">Контакти</h1>

            <div className="flex flex-col items-center space-y-6 text-lg">
                <div className="flex items-center gap-4">
                    <Phone className="text-primary" />
                    <span>+380 99 123 45 67</span>
                </div>

                <div className="flex items-center gap-4">
                    <Mail className="text-primary" />
                    <span>info@soilscout.ua</span>
                </div>

                <div className="flex items-center gap-4">
                    <MapPin className="text-primary" />
                    <span>м. Київ, вул. Інноваційна, 1</span>
                </div>

                {/* Можна вставити карту тут */}
                {/* <iframe src="google-maps-link" className="w-full h-96 rounded-xl"></iframe> */}
            </div>
        </div>
    );
};

export default ContactPage;
