import React, { useEffect, useState } from 'react';
import api from '../utils/api';

const Profile = () => {
    const [user, setUser] = useState(null);
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [hasPassword, setHasPassword] = useState(false);
    const [profilePicture, setProfilePicture] = useState(null);

    const [currentPassword, setCurrentPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [message, setMessage] = useState("");

    // Валідація форми перед збереженням
    const validateForm = () => {
        if (newPassword || confirmPassword) {
            if (!currentPassword) {
                setMessage("Поточний пароль обов'язковий для зміни пароля, або очистіть всі поля пароля.");
                return false;
            }
            if (newPassword !== confirmPassword) {
                setMessage("Новий пароль не співпадає з підтвердженням.");
                return false;
            }
        }

        return true;
    };


    useEffect(() => {
        const fetchUser = async () => {
            try {

                const res = await api.get(`/users/me`);
                setUser(res.data);
                setName(res.data.name);
                setEmail(res.data.email);
                setHasPassword(!!res.data.password);
            } catch (error) {
                console.error("Помилка завантаження профілю:", error);
            }
        };

        fetchUser();
    }, []);

    const handleSave = async (e) => {
        e.preventDefault();

        // Якщо валідація не пройдена, не відправляємо запит
        if (!validateForm()) return;

        try {
            const payload = {
                name,
                email,
            };

// Якщо користувач вводить новий пароль — додаємо паролі в payload
            if (newPassword || confirmPassword || currentPassword) {
                payload.currentPassword = currentPassword;
                payload.newPassword = newPassword;
                payload.confirmPassword = confirmPassword;
            }


            await api.put(`/users/${user.id}`, payload);


            setUser((prev) => ({ ...prev, name }));

            setMessage("Зміни успішно збережено.");
            setCurrentPassword("");
            setNewPassword("");
            setConfirmPassword("");
        } catch (err) {
            setMessage(err.response?.data?.message || "Помилка збереження змін.");
        }
    };


    if (!user) return <p>Завантаження...</p>;

    return (
        <div className="w-full md:w-2/3 p-6">
            <h2 className="text-2xl font-semibold mb-4">Мій акаунт</h2>

            <form onSubmit={handleSave} className="space-y-4">
                <div>
                    <label className="block font-medium text-gray-700 mb-1">Фото профілю</label>
                    <input
                        type="file"
                        accept="image/*"
                        onChange={(e) => setProfilePicture(e.target.files[0])}
                        className="w-full"
                    />
                    {user.profile_picture_url && (
                        <img
                            src={user.profile_picture_url}
                            alt="Фото профілю"
                            className="w-24 h-24 rounded-full mt-2 object-cover"
                        />
                    )}
                </div>

                <div>
                    <label className="block font-medium text-gray-700 mb-1">Email</label>
                    <input
                        type="email"
                        value={email}
                        disabled
                        className="w-full px-4 py-2 border border-gray-300 rounded bg-gray-100 cursor-not-allowed"
                    />
                </div>

                <div>
                    <label className="block font-medium text-gray-700 mb-1">Ім’я</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded"
                    />
                </div>

                {hasPassword && (
                    <>
                        <hr className="my-4" />
                        <h3 className="text-lg font-semibold">Зміна пароля</h3>

                        <div>
                            <label className="block font-medium text-gray-700 mb-1">Поточний пароль</label>
                            <input
                                type="password"
                                value={currentPassword}
                                onChange={(e) => setCurrentPassword(e.target.value)}
                                className="w-full px-4 py-2 border border-gray-300 rounded"
                            />
                        </div>

                        <div>
                            <label className="block font-medium text-gray-700 mb-1">Новий пароль</label>
                            <input
                                type="password"
                                value={newPassword}
                                onChange={(e) => setNewPassword(e.target.value)}
                                className="w-full px-4 py-2 border border-gray-300 rounded"
                            />
                        </div>

                        <div>
                            <label className="block font-medium text-gray-700 mb-1">Підтвердження пароля</label>
                            <input
                                type="password"
                                value={confirmPassword}
                                onChange={(e) => setConfirmPassword(e.target.value)}
                                className="w-full px-4 py-2 border border-gray-300 rounded"
                            />
                        </div>
                    </>
                )}

                {message && <p className="text-sm text-blue-600">{message}</p>}

                <button
                    type="submit"
                    className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700 transition"
                >
                    Зберегти зміни
                </button>
            </form>
        </div>
    );
};

export default Profile;
