import React, { useState, useEffect } from 'react';
// axios та api можуть більше не знадобитися, якщо ви не робите запити прямо з UserMenu
// import axios from 'axios';
// import api from '../utils/api';
import { useTranslation } from "react-i18next";
import { useUserContext } from '../contexts/UserContext'; // Імпортуємо контекст користувача

// const apiBaseUrl = process.env.REACT_APP_API_BASE_URL; // Може не знадобитися, якщо посилання формується в іншому місці або в контексті

export default function UserMenu() {
    // Отримуємо об'єкт користувача безпосередньо з контексту
    const { user } = useUserContext(); // Тепер UserMenu "слухає" зміни в контексті

    const { t } = useTranslation();
    const [isMenuVisible, setIsMenuVisible] = useState(false);
    const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);

    // useEffect, який читав з localStorage, більше не потрібен,
    // бо дані користувача будуть надходити з контексту.
    // localStorage тепер керується всередині UserContext або handleLoginSuccess.
    /*
    useEffect(() => {
       // ... старий код читання з localStorage ...
    }, []);
    */

    // Якщо вихід робиться через контекст, можливо, цю функцію варто теж перенести
    // або викликати функцію виходу з контексту тут.
    const handleLogoutClick = () => {
        setShowLogoutConfirm(true);
    };

    // Припустимо, ваш UserContext має функцію logout
    const { logout } = useUserContext(); // Отримуємо функцію виходу з контексту

    const confirmLogout = async () => {
        try {
            // Викликаємо функцію виходу з контексту
            await logout(); // Ця функція має зробити запит на бекенд і очистити localStorage/контекст
            // Перенаправлення, ймовірно, відбувається всередині функції logout контексту
            // window.location.href = '/login'; // Цей рядок може бути зайвим, якщо контекст вже перенаправляє
        } catch (error) {
            console.error('Logout failed:', error);
            // Обробка помилки виходу
        } finally {
            setShowLogoutConfirm(false);
        }
    };


    const cancelLogout = () => {
        setShowLogoutConfirm(false);
    };

    // Якщо об'єкт користувача ще не завантажено в контексті (наприклад, при першому завантаженні сторінки),
    // можна не відображати меню користувача або відображати плейсхолдер
    if (!user) {
        // Можливо, показати спіннер або нічого не показувати, поки дані не завантажені
        return null; // Або <p>Loading user...</p>;
    }


    return (
        <div className="flex relative inline-block pr-6">
            <div className="relative text-sm">
                {/* Використовуємо user?.name з контексту */}
                <button className="flex items-center focus:outline-none mr-3" onClick={toggleMenu}>
                    <span className="hidden md:inline-block">Hi, {user?.name || 'Guest'}</span> {/* Використовуємо user?.name */}

                    {/* Перевіряємо наявність user?.profile_picture_url з контексту */}
                    {user?.profile_picture_url && (
                        <img
                            // Формуємо SRC, використовуючи шлях з контексту
                            src={`http://localhost:5000${user.profile_picture_url}`}
                            // alt текст для доступності, використовуємо user?.name
                            alt={user?.name ? `Current photo of ${user.name}` : 'User Avatar'}
                            className="w-8 h-8 rounded-full ml-2 object-cover border border-gray-300"
                        />
                    )}
                    {/* SVG іконка залишається */}
                    <svg className="pl-2 h-2" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 129 129">
                        <path d="m121.3,34.6c-1.6-1.6-4.2-1.6-5.8,0l-51,51.1-51.1-51.1c-1.6-1.6-4.2-1.6-5.8,0-1.6,1.6-1.6,4.2 0,5.8l53.9,53.9c0.8,0.8 1.8,1.2 2.9,1.2 1,0 2.1-0.4 2.9-1.2l53.9-53.9c1.7-1.6 1.7-4.2 0.1-5.8z"/>
                    </svg>
                </button>

                {isMenuVisible && (
                    <div className="bg-white nunito rounded shadow-md mt-2 absolute mt-12 top-15 right-0 min-w-full z-30">
                        <ul className="list-reset">
                            <li><a href="/profile" className="px-3 py-2 block text-gray-900 hover:bg-indigo-400 hover:text-white">{t('my_acc')}</a></li>
                            <li><hr className="border-t mx-2 border-gray-400" /></li>
                            {/* Кнопка виходу */}
                            <li><button
                                onClick={handleLogoutClick}
                                className="w-full text-left px-4 py-2 block text-gray-900 hover:bg-red-400 hover:text-white"
                            >
                                {t('logout')}
                            </button></li>
                        </ul>
                    </div>
                )}

                {/* Модальне вікно підтвердження виходу */}
                {showLogoutConfirm && (
                    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
                        <div className="bg-white rounded-lg p-6 w-96 shadow-lg">
                            <h2 className="text-lg font-semibold mb-4">{t('logout_text')}</h2>
                            <div className="flex justify-end space-x-4">
                                <button
                                    onClick={cancelLogout}
                                    className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
                                >
                                    {t('no')}
                                </button>
                                <button
                                    onClick={confirmLogout} // Викликаємо функцію confirmLogout
                                    className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
                                >
                                    {t('yes')}
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}
