// src/components/AnalyticsContent.js
import React, { useEffect, useState } from 'react';
import api from '../utils/api';
// useFieldContext більше не потрібен тут

// Приймаємо пропс selectedFieldId
const AnalyticsContent = ({ selectedFieldId }) => {
    const [timeRange, setTimeRange] = useState('7d'); // Стан для діапазону часу
    const [chartData, setChartData] = useState(null); // Стан для даних графіка
    const [isLoading, setIsLoading] = useState(false); // Стан для індикатора завантаження
    const [error, setError] = useState(null); // Стан для помилки

    // ✅ Функція для вибору поля на бекенді
    // Розміщуємо всередині компонента, щоб мати доступ до api
    const selectField = async (fieldId) => {
        if (!fieldId) return; // Не намагаємося вибрати, якщо ID немає
        try {
            console.log(`Відправка запиту на select поля ID: ${fieldId}`);
            await api.post('/fields/select/', { fieldId });
            console.log(`Поле ID ${fieldId} успішно вибрано.`);
        } catch (err) {
            console.error(`Не вдалося вибрати поле ID ${fieldId}:`, err);
            // Можна додати обробку помилок для користувача, якщо потрібно
        }
    };

    // ✅ Функція для деселекту поля на бекенді
    const deselectField = async (fieldId) => {
        if (!fieldId) return; // Не намагаємося деселектнути, якщо ID немає
        try {
            console.log(`Відправка запиту на deselect поля ID: ${fieldId}`);
            // Припускаємо, що API deselect також приймає fieldId, хоча деякі API можуть деселектити просто "поточне вибране"
            await api.post('/fields/deselect/', { fieldId });
            console.log(`Поле ID ${fieldId} успішно деселектнуто.`);
        } catch (err) {
            console.error(`Не вдалося деселектнути поле ID ${fieldId}:`, err);
            // Можна додати обробку помилок для користувача, якщо потрібно
        }
    };


    // Ефект для завантаження даних та керування станом "вибраності" поля
    useEffect(() => {
        // Цей ефект спрацьовує при зміні selectedFieldId або timeRange

        // Спочатку перевіряємо, чи є вибране поле
        if (!selectedFieldId) {
            // Якщо selectedFieldId став null (наприклад, перейшли на сторінку "/analytics"),
            // очищаємо дані графіка та стан завантаження/помилки
            setChartData(null);
            setError(null);
            setIsLoading(false);
            // Деселект поточного поля буде виконано в cleanup функції попереднього рендеру
            return;
        }

        // ✅ Викликаємо selectField для нового selectedFieldId
        selectField(selectedFieldId);

        // ✅ Після вибору поля, завантажуємо дані графіка
        setIsLoading(true);
        setError(null); // Скидаємо попередні помилки
        const apiUrl = `/field-measurements/chart/${selectedFieldId}/${timeRange}`;

        api.get(apiUrl)
            .then(response => {
                setChartData(response.data); // Зберігаємо отримані дані
                setIsLoading(false);
            })
            .catch(err => {
                console.error('Error fetching chart data:', err);
                setError('Не вдалося завантажити дані графіка.');
                setChartData(null); // Очищаємо дані при помилці
                setIsLoading(false);
            });

        // ✅ Cleanup функція: викликається при зміні selectedFieldId або при демонтажі компонента
        // Вона деселектить поле, яке було вибрано ДО поточного оновлення або демонтажу
        const fieldIdToDeselect = selectedFieldId; // Зберігаємо ID для cleanup
        return () => {
            // Ця функція виконається перед тим, як ефект запуститься знову (якщо змінилися залежності)
            // або коли компонент буде видалено з DOM
            deselectField(fieldIdToDeselect);
        };

    }, [selectedFieldId, timeRange]); // Залежності ефекту: перезапускаємо ефект при зміні selectedFieldId або timeRange

    // ✅ Функція для зміни діапазону часу (залишається без змін)
    const handleTimeRangeChange = (newTimeRange) => {
        setTimeRange(newTimeRange);
    };


    return (
        <div id="main-content" className="w-full flex-1">
            <div className="flex flex-1 flex-wrap">
                <div className="w-full xl:w-2/3 p-6 xl:max-w-6xl">
                    <div className="max-w-full lg:max-w-3xl xl:max-w-5xl">
                        <div className="border-b p-3">
                            {/* Відображаємо ID поля, якщо воно вибрано */}
                            <h5 className="font-bold text-black">Графік вимірювань {selectedFieldId ? `для поля ID: ${selectedFieldId}` : ''}</h5>
                        </div>
                        <div className="p-5">
                            {/* Кнопки вибору масштабування */}
                            <div className="flex space-x-4 mb-4">
                                <button
                                    onClick={() => handleTimeRangeChange('7d')}
                                    className={`px-4 py-2 rounded ${timeRange === '7d' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    Тиждень (7д)
                                </button>
                                <button
                                    onClick={() => handleTimeRangeChange('6m')}
                                    className={`px-4 py-2 rounded ${timeRange === '6m' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    6 місяців (6м)
                                </button>
                                <button
                                    onClick={() => handleTimeRangeChange('1y')}
                                    className={`px-4 py-2 rounded ${timeRange === '1y' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    Рік (1р)
                                </button>
                            </div>

                            {/* Область для відображення графіка */}
                            <div className="ct-chart ct-golden-section" id="chart1">
                                {/* Умови відображення повідомлень залежно від стану */}
                                {!selectedFieldId && (
                                    <p className="text-center text-gray-500">Будь ласка, оберіть поле зі списку ліворуч, щоб побачити графік.</p>
                                )}
                                {selectedFieldId && isLoading && (
                                    <p className="text-center text-gray-500">Завантаження даних графіка...</p>
                                )}
                                {selectedFieldId && error && (
                                    <p className="text-center text-red-500">{error}</p>
                                )}
                                {/* Перевіряємо, чи є вибране поле, чи не завантажується, немає помилок, і дані порожні/відсутні */}
                                {selectedFieldId && !isLoading && !error && (!chartData || (Array.isArray(chartData.series) && chartData.series.every(s => s.length === 0))) && (
                                    <p className="text-center text-gray-500">Дані для обраного поля за цей період не знайдені.</p>
                                )}
                                {/* Тут буде код для рендерингу графіка, коли chartData є */}
                                {/* Наприклад: {selectedFieldId && !isLoading && !error && chartData && <YourChartComponent data={chartData} />} */}

                            </div>
                        </div>

                        {/* Розділ таблиці (залишаємо як було) */}
                        <div className="p-3">
                            <div className="border-b p-3">
                                <h5 className="font-bold text-black">Table</h5>
                            </div>
                            <div className="p-5">
                                <table className="w-full p-5 text-gray-700">
                                    <thead>
                                    <tr>
                                        <th className="text-left text-blue-900">Name</th>
                                        <th className="text-left text-blue-900">Side</th>
                                        <th className="text-left text-blue-900">Role</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    <tr>
                                        <td>Obi Wan Kenobi</td>
                                        <td>Light</td>
                                        <td>Jedi</td>
                                    </tr>
                                    <tr>
                                        <td>Greedo</td>
                                        <td>South</td>
                                        <td>Scumbag</td>
                                    </tr>
                                    <tr>
                                        <td>Darth Vader</td>
                                        <td>Dark</td>
                                        <td>Sith</td>
                                    </tr>
                                    </tbody>
                                </table>
                                <p className="py-2"><a href="#">See More issues...</a></p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default AnalyticsContent;
