import React, { useContext, useEffect, useState } from 'react';
import {Navigate, useNavigate} from 'react-router-dom';
import { UserContext } from '../contexts/UserContext';

const ProtectedRoute = ({ children }) => {
    const { user, hasDevice, loading } = useContext(UserContext);
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();
    useEffect(() => {
        // Якщо користувач ще не завантажений або в процесі завантаження, не робимо перевірку
        if (loading) {
            setErrorMessage('Завантаження...');
            return;
        }

        // Якщо немає користувача, то треба перенаправити на сторінку логіну
        if (!user) {
            alert('Користувач не авторизований');
            navigate('/login');
        }

        // Якщо немає пристроїв, перенаправляємо на публічну сторінку покупки пристрою
        if (!hasDevice) {
            setErrorMessage('Ви не маєте пристроїв. Придбайте пристрій, щоб продовжити.');
            alert('Придбайте спочатку девайс');
            return <Navigate to="/buy-device" replace />;
        }

        // Якщо всі умови виконуються, очищаємо повідомлення про помилку
        setErrorMessage('');
    }, [user, hasDevice, loading]);

    if (errorMessage) {
        console.log(errorMessage); // Логуємо повідомлення
        return <div>{errorMessage}</div>;
    }

    // Якщо все нормально, рендеримо дочірній компонент
    return children;
};

export default ProtectedRoute;
