import React, { useState } from 'react';
import {useTranslation} from "react-i18next";
export default function AuthForm({ type, onSubmit, onGoogleLogin, isSubmitting }) { // Додано isSubmitting
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState(''); // *** НОВИЙ СТАН ДЛЯ ІМЕНІ ***
    const { t, i18n } = useTranslation();

    const handleSubmit = e => {
        e.preventDefault();
        // *** ЗБИРАЄМО ДАНІ ФОРМИ В ОБ'ЄКТ ***
        const formData = {
            email,
            password,
        };
        if (type === 'register') {
            formData.name = name; // *** ДОДАЄМО ІМ'Я, ЯКЩО ЦЕ РЕЄСТРАЦІЯ ***
        }
        // *** Передаємо об'єкт з даними до батьківського компонента через onSubmit ***
        onSubmit(formData);
    };

    return (
        <form onSubmit={handleSubmit} className="flex flex-col gap-4 max-w-sm mx-auto mt-10">
            <h2 className="text-2xl font-bold text-center">
                {type === 'login' ? 'Логін' : 'Реєстрація'}
            </h2>

            {/* *** ПОЛЕ ДЛЯ ІМЕНІ - ВІДОБРАЖАЄМО ЛИШЕ ПРИ РЕЄСТРАЦІЇ *** */}
            {type === 'register' && (
                <input
                    type="text"
                    placeholder="Ваше ім’я" // Змінено плейсхолдер
                    value={name}
                    onChange={e => setName(e.target.value)}
                    className="border p-2 rounded"
                    required // Робимо поле обов'язковим, оскільки бекенд цього вимагає
                />
            )}
            {/* *** КІНЕЦЬ ПОЛЯ ДЛЯ ІМЕНІ *** */}


            <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={e => setEmail(e.target.value)}
                className="border p-2 rounded"
                required
            />
            <input
                type="password"
                placeholder="Пароль"
                value={password}
                onChange={e => setPassword(e.target.value)}
                className="border p-2 rounded"
                required
            />

            {/* Використовуємо пропс isSubmitting для вимкнення кнопки під час відправки */}
            <button type="submit" className="bg-blue-500 text-white py-2 rounded hover:bg-blue-600 disabled:opacity-50" disabled={isSubmitting}>
                {type === 'login' ? (isSubmitting ? 'Вхід...' : 'Увійти') : (isSubmitting ? 'Реєстрація...' : 'Зареєструватися')}
            </button>

            {/* Використовуємо пропс isSubmitting для вимкнення кнопки Google під час відправки */}
            <button
                type="button"
                onClick={onGoogleLogin}
                className="bg-red-500 hover:bg-red-600 text-white py-2 rounded disabled:opacity-50"
                disabled={isSubmitting}
            >
                {type === 'login' ? t(`sign_in_google`) : t(`sign_up_google`)}
            </button>


        </form>
    );
}
