import React, { useState } from "react";
import { GoogleOAuthProvider, GoogleLogin } from "@react-oauth/google";

const RegisterModal = () => {
    const [isOpen, setIsOpen] = useState(false);

    const closeModal = () => setIsOpen(false);
    const openModal = () => setIsOpen(true);

    const handleGoogleLoginSuccess = (response) => {
        console.log("Google ID Token:", response.credential);
        // Надішліть ID Token на сервер для верифікації
        fetch("http://localhost:5000/api/auth/register/google", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ token: response.credential }),
        })
            .then((res) => res.json())
            .then((data) => {
                console.log("Результат авторизації:", data);
                closeModal(); // Закриваємо модальне вікно після успішної реєстрації
            })
            .catch((err) => console.error("Помилка:", err));
    };

    return (
        <GoogleOAuthProvider clientId="106272488458138782850">
            <button
                onClick={openModal}
                className="bg-green-500 text-white px-6 py-3 rounded-md hover:bg-green-600"
            >
                Реєстрація
            </button>

            {isOpen && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
                    <div className="bg-white w-96 p-8 rounded-md shadow-md relative">
                        {/* Кнопка закриття */}
                        <button
                            onClick={closeModal}
                            className="absolute top-2 right-2 text-gray-800 hover:text-gray-900 text-2xl"
                        >
                            &times;
                        </button>

                        <h2 className="text-2xl font-bold text-center mb-4">Реєстрація</h2>
                        <form className="space-y-4">
                            <div>
                                <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                                    Email
                                </label>
                                <input
                                    type="email"
                                    id="email"
                                    className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="Введіть ваш email"
                                />
                            </div>
                            <div>
                                <label htmlFor="password" className="block text-sm font-medium text-gray-700">
                                    Пароль
                                </label>
                                <input
                                    type="password"
                                    id="password"
                                    className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="Введіть ваш пароль"
                                />
                            </div>
                            <button
                                type="submit"
                                className="w-full py-2 px-4 bg-blue-600 text-white font-semibold rounded-md shadow hover:bg-blue-700"
                            >
                                Зареєструватися
                            </button>
                        </form>
                        <div className="mt-6 text-center">
                            <p className="text-sm text-gray-500">або</p>
                            <div className="mt-2 w-full">
                                <GoogleLogin
                                    onSuccess={handleGoogleLoginSuccess}
                                    onError={() => console.log("Помилка під час входу через Google")}
                                />
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </GoogleOAuthProvider>
    );
};

export default RegisterModal;
